import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import axios from 'axios';

const Subscriptions = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subscribing, setSubscribing] = useState(false);
  const [activeSubscription, setActiveSubscription] = useState(null);
  const [showConfirmCancel, setShowConfirmCancel] = useState(false);
  const { isAuthenticated, user, checkSubscription } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect admin and superadmin users away from the subscriptions page
    if (isAuthenticated && user && (user.role === 'admin' || user.role === 'superadmin')) {
      navigate('/browse');
      return;
    }

    fetchSubscriptionPlans();
    if (isAuthenticated) {
      fetchUserSubscription();
    }
  }, [isAuthenticated, user, navigate]);

  const fetchSubscriptionPlans = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/subscriptions/plans');
      setPlans(response.data);
    } catch (err) {
      setError('Failed to load subscription plans. Please try again later.');
      console.error('Error fetching plans:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserSubscription = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/subscriptions/me');
      setActiveSubscription(response.data);
    } catch (err) {
      // Only set error if it's not a 404 (no subscription found)
      if (err.response && err.response.status !== 404) {
        setError('Failed to load your subscription. Please try again later.');
        console.error('Error fetching user subscription:', err);
      }
    }
  };

  const handleSubscribe = async (planId) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      setSubscribing(true);
      await axios.post('http://localhost:5000/api/subscriptions', { plan_id: planId });

      // Update user subscription status
      await checkSubscription();

      // Fetch the updated subscription
      await fetchUserSubscription();

      // Navigate to browse page
      navigate('/browse');
    } catch (err) {
      setError('Failed to process subscription. Please try again later.');
      console.error('Error subscribing:', err);
    } finally {
      setSubscribing(false);
    }
  };

  const handleCancelSubscription = async () => {
    try {
      setSubscribing(true);
      await axios.delete('http://localhost:5000/api/subscriptions/me');
      setActiveSubscription(null);
      await checkSubscription();
      setShowConfirmCancel(false);
    } catch (err) {
      setError('Failed to cancel subscription. Please try again later.');
      console.error('Error cancelling subscription:', err);
    } finally {
      setSubscribing(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gray-50">
        <div className="flex flex-col items-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-red-600 mb-4"></div>
          <div className="text-xl text-gray-700">Loading subscription plans...</div>
        </div>
      </div>
    );
  }

  // Function to get the features for comparison
  const getAllFeatures = () => {
    const allFeatures = new Set();
    plans.forEach(plan => {
      plan.features.forEach(feature => {
        allFeatures.add(feature);
      });
    });
    return Array.from(allFeatures);
  };

  // Check if a plan has a specific feature
  const planHasFeature = (plan, feature) => {
    return plan.features.includes(feature);
  };

  return (
    <div className="bg-gradient-to-b from-gray-50 to-white min-h-screen">
      <div className="container mx-auto px-4 py-12">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold mb-4 text-gray-900 tracking-tight">Choose Your Plan</h1>
          <p className="text-lg text-gray-700 max-w-2xl mx-auto leading-relaxed">
            Get unlimited access to our entire library of movies and TV shows with a premium VortexTV subscription.
          </p>
        </div>

        {error && (
          <div className="max-w-md mx-auto mb-8 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg shadow-sm animate-fade-in">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium">{error}</p>
              </div>
            </div>
          </div>
        )}

        {activeSubscription && (
          <div className="max-w-3xl mx-auto mb-16 bg-white border border-gray-200 p-8 rounded-xl shadow-md transform hover:shadow-lg transition-all duration-300">
            <div className="flex flex-col md:flex-row items-start md:items-center justify-between">
              <div>
                <div className="flex items-center mb-2">
                  <h2 className="text-2xl font-bold text-gray-900 mr-3">Your Current Subscription</h2>
                  <span className="bg-green-100 text-green-800 text-xs font-semibold px-2.5 py-0.5 rounded-full">
                    {activeSubscription.is_active ? 'Active' : 'Inactive'}
                  </span>
                </div>
                <div className="mb-4">
                  <p className="text-xl font-medium text-gray-800 mb-1">
                    {activeSubscription.plan_name} Plan
                  </p>
                  <p className="text-gray-600 flex items-center">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                    </svg>
                    Expires on: {new Date(activeSubscription.end_date).toLocaleDateString('en-US', { 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric' 
                    })}
                  </p>
                </div>
              </div>
              {!showConfirmCancel ? (
                <button
                  onClick={() => setShowConfirmCancel(true)}
                  className="bg-white border border-gray-300 hover:bg-gray-50 text-gray-700 font-medium px-4 py-2 rounded-lg shadow-sm transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                >
                  Cancel Subscription
                </button>
              ) : (
                <div className="flex space-x-2 mt-4 md:mt-0">
                  <button
                    onClick={() => setShowConfirmCancel(false)}
                    className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-4 py-2 rounded-lg transition-colors"
                  >
                    Keep Subscription
                  </button>
                  <button
                    onClick={handleCancelSubscription}
                    disabled={subscribing}
                    className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg shadow-sm transition-colors flex items-center"
                  >
                    {subscribing ? (
                      <>
                        <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                        Processing...
                      </>
                    ) : (
                      'Confirm Cancellation'
                    )}
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Desktop View: Table Comparison for larger screens */}
        <div className="hidden lg:block mb-12">
          <div className="max-w-6xl mx-auto bg-white rounded-xl overflow-hidden shadow-md">
            <table className="w-full text-left border-collapse">
              <thead>
                <tr className="bg-gray-50">
                  <th className="py-4 px-6 w-1/4">Plan Features</th>
                  {plans.map(plan => (
                    <th key={plan.plan_id} className={`py-4 px-6 text-center ${plan.plan_name === 'Standard' ? 'bg-red-50' : ''}`}>
                      <div className={`text-xl font-bold ${plan.plan_name === 'Standard' ? 'text-red-600' : 'text-gray-900'}`}>{plan.plan_name}</div>
                      <div className="text-2xl font-bold text-gray-900 mt-1">{plan.price}<span className="text-sm font-normal text-gray-600">/mo</span></div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {getAllFeatures().map((feature, index) => (
                  <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                    <td className="py-3 px-6 font-medium text-gray-800">{feature}</td>
                    {plans.map(plan => (
                      <td key={`${plan.plan_id}-${index}`} className={`py-3 px-6 text-center ${plan.plan_name === 'Standard' ? (index % 2 === 0 ? 'bg-red-50' : 'bg-white') : ''}`}>
                        {planHasFeature(plan, feature) ? (
                          <svg className="h-5 w-5 text-green-500 mx-auto" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                          </svg>
                        ) : (
                          <svg className="h-5 w-5 text-gray-300 mx-auto" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                          </svg>
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
                <tr>
                  <td className="py-4 px-6"></td>
                  {plans.map(plan => (
                    <td key={`${plan.plan_id}-action`} className={`py-4 px-6 text-center ${plan.plan_name === 'Standard' ? 'bg-red-50' : ''}`}>
                      <button
                        onClick={() => handleSubscribe(plan.plan_id)}
                        disabled={subscribing || (activeSubscription && activeSubscription.plan_name === plan.plan_name)}
                        className={`w-full py-2 px-4 rounded-lg font-medium transition-all duration-300 transform hover:scale-105 ${
                          activeSubscription && activeSubscription.plan_name === plan.plan_name
                            ? 'bg-green-600 text-white cursor-default'
                            : plan.plan_name === 'Standard'
                              ? 'bg-red-600 hover:bg-red-700 text-white shadow-md'
                              : 'bg-gray-800 hover:bg-gray-900 text-white'
                        }`}
                      >
                        {subscribing
                          ? 'Processing...'
                          : activeSubscription && activeSubscription.plan_name === plan.plan_name
                          ? 'Current Plan'
                          : 'Select Plan'}
                      </button>
                    </td>
                  ))}
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        {/* Mobile View: Cards for smaller screens */}
        <div className="lg:hidden grid grid-cols-1 md:grid-cols-3 gap-8">
          {plans.map((plan) => (
            <div 
              key={plan.plan_id} 
              className={`relative bg-white p-8 rounded-xl shadow-md hover:shadow-lg transition-all duration-300 flex flex-col ${
                plan.plan_name === 'Standard' 
                  ? 'border-2 border-red-500 transform scale-105 z-10' 
                  : activeSubscription && activeSubscription.plan_name === plan.plan_name
                  ? 'border-2 border-green-500'
                  : ''
              }`}
            >
              {plan.plan_name === 'Standard' && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 bg-red-600 text-white px-6 py-1 rounded-full text-sm font-bold shadow-md">
                  POPULAR
                </div>
              )}

              {activeSubscription && activeSubscription.plan_name === plan.plan_name && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 bg-green-600 text-white px-6 py-1 rounded-full text-sm font-bold shadow-md">
                  CURRENT PLAN
                </div>
              )}

              <h3 className={`text-2xl font-bold mb-2 ${plan.plan_name === 'Standard' ? 'text-red-600' : 'text-gray-900'}`}>
                {plan.plan_name}
              </h3>
              <p className="text-3xl font-bold mb-4 text-gray-900">
                {plan.price}<span className="text-sm font-normal text-gray-600">/month</span>
              </p>
              <p className="text-gray-700 mb-6">{plan.description}</p>

              <ul className="mb-8 flex-grow space-y-3">
                {plan.features.map((feature, index) => (
                  <li key={index} className="flex items-start text-gray-800">
                    <span className="mr-2 mt-1 text-green-600">
                      <svg className="h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                    </span> 
                    <span>{feature}</span>
                  </li>
                ))}
              </ul>

              <button
                onClick={() => handleSubscribe(plan.plan_id)}
                disabled={subscribing || (activeSubscription && activeSubscription.plan_name === plan.plan_name)}
                className={`w-full py-3 px-4 rounded-lg font-medium transition-all duration-300 transform hover:scale-105 ${
                  activeSubscription && activeSubscription.plan_name === plan.plan_name
                    ? 'bg-green-600 text-white cursor-default'
                    : plan.plan_name === 'Standard'
                      ? 'bg-red-600 hover:bg-red-700 text-white shadow-md'
                      : 'bg-gray-800 hover:bg-gray-900 text-white'
                }`}
              >
                {subscribing
                  ? 'Processing...'
                  : activeSubscription && activeSubscription.plan_name === plan.plan_name
                  ? 'Current Plan'
                  : 'Select Plan'}
              </button>
            </div>
          ))}
        </div>

        <div className="mt-16 text-center max-w-4xl mx-auto">
          <h3 className="text-2xl font-bold mb-6 text-gray-900">All Plans Include:</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-all duration-300">
              <div className="text-red-500 mb-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div className="font-medium text-lg mb-2 text-gray-800">No Contracts</div>
              <p className="text-gray-600">Cancel anytime, no hidden fees or long-term commitments</p>
            </div>
            <div className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-all duration-300">
              <div className="text-red-500 mb-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 4v16M17 4v16M3 8h4m10 0h4M3 12h18M3 16h4m10 0h4M4 20h16a1 1 0 001-1V5a1 1 0 00-1-1H4a1 1 0 00-1 1v14a1 1 0 001 1z" />
                </svg>
              </div>
              <div className="font-medium text-lg mb-2 text-gray-800">All Content</div>
              <p className="text-gray-600">Access to thousands of movies and TV shows in our library</p>
            </div>
            <div className="bg-white p-6 rounded-xl shadow-sm hover:shadow-md transition-all duration-300">
              <div className="text-red-500 mb-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 4a2 2 0 114 0v1a1 1 0 001 1h3a1 1 0 011 1v3a1 1 0 01-1 1h-1a2 2 0 100 4h1a1 1 0 011 1v3a1 1 0 01-1 1h-3a1 1 0 01-1-1v-1a2 2 0 10-4 0v1a1 1 0 01-1 1H7a1 1 0 01-1-1v-3a1 1 0 00-1-1H4a2 2 0 110-4h1a1 1 0 001-1V7a1 1 0 011-1h3a1 1 0 001-1V4z" />
                </svg>
              </div>
              <div className="font-medium text-lg mb-2 text-gray-800">No Ads</div>
              <p className="text-gray-600">Enjoy an uninterrupted and premium streaming experience</p>
            </div>
          </div>
        </div>

        <div className="mt-16 bg-gray-50 rounded-xl p-8 max-w-4xl mx-auto border border-gray-100">
          <h3 className="text-xl font-semibold mb-4 text-gray-900">Frequently Asked Questions</h3>
          <div className="space-y-4">
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Can I change my plan later?</h4>
              <p className="text-gray-600">Yes, you can upgrade or downgrade your plan at any time. Changes will take effect on your next billing cycle.</p>
            </div>
            <div>
              <h4 className="font-medium text-gray-900 mb-1">How do I cancel my subscription?</h4>
              <p className="text-gray-600">You can cancel your subscription at any time through your account settings. Your subscription will remain active until the end of your current billing period.</p>
            </div>
            <div>
              <h4 className="font-medium text-gray-900 mb-1">Are there any commitments?</h4>
              <p className="text-gray-600">No, all our plans are month-to-month with no long-term commitments. You can cancel anytime.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Subscriptions;
