import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const MovieDetails = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [similarMovies, setSimilarMovies] = useState([]);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/api/movies/${id}`);
        setMovie(response.data);
        
        // Fetch similar movies
        const similarResponse = await axios.get(`http://localhost:5000/api/movies/${id}/similar`);
        setSimilarMovies(similarResponse.data.slice(0, 6));
      } catch (err) {
        console.error('Error fetching movie details:', err);
        setError('Failed to load movie details. Please try again later.');
        
        // Set dummy data for development/preview
        setMovie({
          id: parseInt(id),
          title: 'Sample Movie Title',
          poster_path: `https://via.placeholder.com/500x750?text=Movie+${id}`,
          backdrop_path: `https://via.placeholder.com/1280x720?text=Movie+${id}+Backdrop`,
          release_date: '2023-05-15',
          runtime: 120,
          vote_average: 8.5,
          vote_count: 1250,
          genres: ['Action', 'Thriller', 'Sci-Fi'],
          overview: 'This is a sample movie overview. It would normally contain a description of the movie plot and other relevant information about the film.',
          director: 'John Director',
          cast: ['Actor One', 'Actor Two', 'Actor Three', 'Actor Four']
        });
        
        setSimilarMovies(Array(6).fill().map((_, idx) => ({
          id: 1000 + idx,
          title: `Similar Movie ${idx + 1}`,
          poster_path: `https://via.placeholder.com/300x450?text=Similar+${idx+1}`,
          vote_average: (Math.random() * 2 + 7).toFixed(1)
        })));
      } finally {
        setLoading(false);
      }
    };
    
    fetchMovieDetails();
  }, [id]);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">Loading movie details...</div>
      </div>
    );
  }

  if (!movie && !loading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
          Movie not found or has been removed.
        </div>
        <Link to="/browse" className="text-red-600 hover:underline">
          &larr; Back to Browse
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Movie Backdrop */}
      <div className="relative">
        <div 
          className="aspect-[21/9] bg-cover bg-center"
          style={{ backgroundImage: `url(${movie.backdrop_path})` }}
        >
          <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent" />
        </div>
      </div>
      
      {/* Movie Content */}
      <div className="container mx-auto px-4 py-12">
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
            {error}
          </div>
        )}
        
        <div className="flex flex-col md:flex-row">
          {/* Movie Poster */}
          <div className="md:w-1/3 lg:w-1/4 mb-8 md:mb-0">
            <img 
              src={movie.poster_path} 
              alt={movie.title} 
              className="w-full rounded-lg shadow-lg"
            />
          </div>
          
          {/* Movie Info */}
          <div className="md:w-2/3 lg:w-3/4 md:pl-8">
            <h1 className="text-3xl md:text-4xl font-bold mb-2">{movie.title}</h1>
            
            <div className="flex items-center mb-4 text-sm text-gray-600">
              <span className="mr-4">{new Date(movie.release_date).getFullYear()}</span>
              {movie.runtime && <span className="mr-4">{movie.runtime} min</span>}
              <span className="flex items-center text-yellow-500">
                <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                </svg>
                {movie.vote_average} ({movie.vote_count} votes)
              </span>
            </div>
            
            <div className="mb-6">
              {movie.genres && movie.genres.map((genre, index) => (
                <span 
                  key={index} 
                  className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2"
                >
                  {genre}
                </span>
              ))}
            </div>
            
            <div className="mb-6">
              <h2 className="text-xl font-bold mb-2">Overview</h2>
              <p className="text-gray-700">{movie.overview}</p>
            </div>
            
            {movie.director && (
              <div className="mb-6">
                <h2 className="text-xl font-bold mb-2">Director</h2>
                <p className="text-gray-700">{movie.director}</p>
              </div>
            )}
            
            {movie.cast && movie.cast.length > 0 && (
              <div className="mb-6">
                <h2 className="text-xl font-bold mb-2">Cast</h2>
                <div className="flex flex-wrap">
                  {movie.cast.map((actor, index) => (
                    <span 
                      key={index} 
                      className="inline-block bg-gray-100 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2"
                    >
                      {actor}
                    </span>
                  ))}
                </div>
              </div>
            )}
            
            <div className="flex space-x-3 mt-8">
              <button className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded-md font-medium transition-colors">
                Watch Now
              </button>
              <button className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-6 py-2 rounded-md font-medium transition-colors">
                Add to Favorites
              </button>
            </div>
          </div>
        </div>
        
        {/* Similar Movies */}
        {similarMovies.length > 0 && (
          <div className="mt-16">
            <h2 className="text-2xl font-bold mb-6">Similar Movies You Might Like</h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 md:gap-6">
              {similarMovies.map((similarMovie) => (
                <Link to={`/movie/${similarMovie.id}`} key={similarMovie.id} className="group">
                  <div className="relative overflow-hidden rounded-lg mb-2">
                    <img
                      src={similarMovie.poster_path}
                      alt={similarMovie.title}
                      className="w-full h-auto transition-transform duration-300 group-hover:scale-105"
                    />
                    <div className="absolute top-0 right-0 bg-black bg-opacity-75 text-yellow-400 p-1 text-sm">
                      ★ {similarMovie.vote_average}
                    </div>
                  </div>
                  <h3 className="font-medium truncate">{similarMovie.title}</h3>
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MovieDetails;
