import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import tmdbApi, { getImageUrl } from '../api/tmdbApi';
import { useAuth } from '../hooks/useAuth';
import { getAllSubscriptionPlans } from '../api/backendApi';

const Home = () => {
  const { isAuthenticated } = useAuth();
  const [featuredContent, setFeaturedContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [plans, setPlans] = useState([]);
  const [plansLoading, setPlansLoading] = useState(true);

  // Fallback content to use when API is unavailable
  const fallbackContent = [
    { id: 1, type: 'movie', title: 'The Matrix', image: 'https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_.jpg' },
    { id: 2, type: 'movie', title: 'Inception', image: 'https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg' },
    { id: 3, type: 'tv', title: 'Breaking Bad', image: 'https://m.media-amazon.com/images/M/MV5BYmQ4YWMxYjUtNjZmYi00MDQ1LWFjMjMtNjA5ZDdiYjdiODU5XkEyXkFqcGdeQXVyMTMzNDExODE5._V1_.jpg' },
    { id: 4, type: 'tv', title: 'Stranger Things', image: 'https://m.media-amazon.com/images/M/MV5BMDZkYmVhNjMtNWU4MC00MDQxLWE3MjYtZGMzZWI1ZjhlOWJmXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_.jpg' },
  ];

  useEffect(() => {
    const fetchFeaturedContent = async () => {
      try {
        setLoading(true);
        // Set a timeout for the API request
        const timeoutPromise = new Promise((_, reject) => 
          setTimeout(() => reject(new Error('API request timed out')), 5000)
        );

        // Fetch trending movies and popular TV shows with timeout
        const fetchDataPromise = Promise.all([
          tmdbApi.getTrendingMovies(),
          tmdbApi.getPopularTvShows()
        ]);

        // Race between the API request and the timeout
        const [trendingMovies, popularTVShows] = await Promise.race([
          fetchDataPromise,
          timeoutPromise.then(() => { throw new Error('Timeout'); })
        ]);

        // Combine and format the content
        const combinedContent = [
          ...trendingMovies.slice(0, 2).map(movie => ({
            id: movie.id,
            type: 'movie',
            title: movie.title,
            image: getImageUrl(movie.poster_path, 'w500')
          })),
          ...popularTVShows.slice(0, 2).map(show => ({
            id: show.id,
            type: 'tv',
            title: show.name,
            image: getImageUrl(show.poster_path, 'w500')
          }))
        ];

        setFeaturedContent(combinedContent);
        setError(null);
      } catch (err) {
        console.error('Error fetching featured content:', err);
        // Use fallback content in case of error
        setFeaturedContent(fallbackContent);
        setError('Unable to connect to the movie database. Showing sample content instead.');
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedContent();
  }, []);

  useEffect(() => {
    const fetchSubscriptionPlans = async () => {
      try {
        setPlansLoading(true);
        const response = await getAllSubscriptionPlans();
        setPlans(response.data);
      } catch (err) {
        console.error('Error fetching subscription plans:', err);
        // Set dummy data for development/preview
        setPlans([
          {
            plan_id: 1,
            plan_name: 'Basic',
            price: 599,
            duration_months: 1,
            max_access_codes: 1,
            description: 'Perfect for individuals who want to enjoy content on a single device.',
            features: ['HD streaming', 'Watch on 1 device', 'Limited content']
          },
          {
            plan_id: 2,
            plan_name: 'Standard',
            price: 999,
            duration_months: 1,
            max_access_codes: 2,
            description: 'Great for couples or small families with multiple devices.',
            features: ['Full HD streaming', 'Watch on 2 devices', 'All content']
          },
          {
            plan_id: 3,
            plan_name: 'Premium',
            price: 1499,
            duration_months: 1,
            max_access_codes: 4,
            description: 'Ultimate experience for families and enthusiasts.',
            features: ['4K Ultra HD streaming', 'Watch on 4 devices', 'Early access to new releases']
          }
        ]);
      } finally {
        setPlansLoading(false);
      }
    };

    fetchSubscriptionPlans();
  }, []);

  return (
    <div>
      {/* Hero Section */}
      <div className="relative bg-gradient-to-r from-gray-900 via-black to-red-900 text-white overflow-hidden">
        {/* Background Pattern Overlay */}
        <div className="absolute inset-0 opacity-20">
          <div className="absolute inset-0" style={{ 
            backgroundImage: 'radial-gradient(circle, rgba(255,255,255,0.1) 1px, transparent 1px)', 
            backgroundSize: '20px 20px' 
          }}></div>
        </div>

        <div className="container mx-auto px-4 py-32 md:py-40 relative z-10">
          <div className="max-w-3xl mx-auto text-center">
            <h1 className="text-5xl md:text-6xl font-bold mb-6 leading-tight bg-clip-text text-transparent bg-gradient-to-r from-white via-gray-100 to-red-200">
              Welcome to VortexTV
            </h1>

            <p className="text-xl md:text-2xl leading-relaxed mb-10 text-gray-200">
              Your ultimate streaming platform for movies and TV shows. 
              <span className="block mt-2">Experience entertainment without limits.</span>
            </p>

            <div className="flex flex-col sm:flex-row gap-5 justify-center items-center">
              {!isAuthenticated && (
                <Link
                  to="/register"
                  className="w-full sm:w-auto px-10 py-4 rounded-full bg-gradient-to-r from-red-600 to-red-700 text-white font-medium text-lg transition-transform duration-300 hover:shadow-lg hover:from-red-500 hover:to-red-700 transform hover:-translate-y-1 flex justify-center items-center"
                >
                  <span>Sign Up Now</span>
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 ml-2" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L12.586 11H5a1 1 0 110-2h7.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </Link>
              )}
              <Link
                to="/subscriptions"
                className="w-full sm:w-auto px-10 py-4 rounded-full bg-transparent border-2 border-white text-white font-medium text-lg transition-all duration-300 hover:bg-white hover:text-gray-900 flex justify-center items-center"
              >
                <span>View Plans</span>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 ml-2" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M6.672 1.911a1 1 0 10-1.932.518l.259.966a1 1 0 001.932-.518l-.26-.966zM2.429 4.74a1 1 0 10-.517 1.932l.966.259a1 1 0 00.517-1.932l-.966-.26zm8.814-.569a1 1 0 00-1.415-1.414l-.707.707a1 1 0 101.415 1.415l.707-.708zm-7.071 7.072l.707-.707A1 1 0 003.465 9.12l-.708.707a1 1 0 001.415 1.415zm3.2-5.171a1 1 0 00-1.3 1.3l4 10a1 1 0 001.823.075l1.38-2.759 3.018 3.02a1 1 0 001.414-1.415l-3.019-3.02 2.76-1.379a1 1 0 00-.076-1.822l-10-4z" clipRule="evenodd" />
                </svg>
              </Link>
            </div>

            {/* Streaming Platforms */}
            <div className="mt-16 flex flex-wrap justify-center items-center gap-8 opacity-70">
              <div className="text-xs uppercase tracking-wide text-gray-400 mb-2 w-full">Available on multiple platforms</div>
              <div className="h-8 w-8 text-gray-300">
                <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24"><path d="M21.928 11.607c-.202-.488-.635-.605-.928-.633v-8.974h-6v.064c-.217.011-.434.066-.581.218l-7.007 7.007c-.501.501-.501 1.311 0 1.812l4.83 4.83c.501.501 1.311.501 1.812 0l7.007-7.007c.152-.148.207-.364.218-.582h.649v3.265zm-9.928 1.393c-.828 0-1.5-.672-1.5-1.5s.672-1.5 1.5-1.5 1.5.672 1.5 1.5-.672 1.5-1.5 1.5zm-8.5 2.5c.276 0 .5.224.5.5v7.5h6v-7.5c0-.276.224-.5.5-.5s.5.224.5.5v8c0 .276-.224.5-.5.5h-7c-.276 0-.5-.224-.5-.5v-8c0-.276.224-.5.5-.5z"/></svg>
              </div>
              <div className="h-8 w-8 text-gray-300">
                <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24"><path d="M22 17.607c-.786 2.28-3.139 6.317-5.563 6.361-1.608.031-2.125-.953-3.963-.953-1.837 0-2.412.923-3.932.983-2.572.099-6.542-5.827-6.542-10.995 0-4.747 3.308-7.1 6.198-7.143 1.55-.028 3.014 1.045 3.959 1.045.949 0 2.727-1.29 4.596-1.101.782.033 2.979.315 4.389 2.377-3.741 2.442-3.158 7.549.858 9.426zm-5.222-17.607c-2.826.114-5.132 3.079-4.81 5.531 2.612.203 5.118-2.725 4.81-5.531z"/></svg>
              </div>
              <div className="h-8 w-8 text-gray-300">
                <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24"><path d="M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0-2c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm-2 10h-2v2h2v-2zm6-6h-10v10h2v-8h8v-2zm-4 6h-2v2h2v-2zm0-4h-2v2h2v-2z"/></svg>
              </div>
              <div className="h-8 w-8 text-gray-300">
                <svg xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 24 24"><path d="M0 0v24h24v-24h-24zm11.375 17.292c0 .456-.227.792-.689.792-1.016 0-1.301-1.03-1.301-1.792 0-.887.289-1.596 1.189-1.596.922-.001 1.059.904 1.15 1.557h-.74c-.039-.279-.195-.541-.418-.541-.28 0-.416.32-.416.65v.825c0 .316.128.636.428.636.213 0 .382-.203.428-.521h.369zm1.731.649v-2.863h-.666v.307c-.204-.307-.486-.391-.803-.391-.67 0-1.123.518-1.123 1.596 0 .682.377 1.572 1.143 1.572.383 0 .617-.232.783-.44v.219h.666zm-2.678-7.073c-.437.271-.898.399-1.355.399-.584 0-1.068-.218-1.348-.401-.577-.375-.869-.896-.869-1.68 0-.772.275-1.335.869-1.675.565-.323 1.049-.393 1.348-.393.455 0 .912.12 1.355.393l-.545.817c-.193-.106-.454-.214-.748-.214-.496 0-.847.33-.847.898 0 .552.361.939.894.939.177 0 .453-.04.701-.151v-.358h-.598v-.697h1.144v1.45h-.001zm1.843-1.677h-.991v.358h.981v.823h-1.652v-2.936h1.652v.783h-1.031v.396h1.041v.576zm-1.953 8.801c-.773 0-1.399-.786-1.399-1.76s.626-1.76 1.399-1.76c.773 0 1.399.786 1.399 1.76s-.626 1.76-1.399 1.76zm3.026-9.348v2.936h-.747v-2.936h.747zm3.211 10.339h-4.604v-2.771h.75v2.094h3.135v-2.094h.719v2.771zm-1.267-6.454h-1.031v.396h1.041v.575h-.991v.358h.981v.824h-1.652v-2.937h1.652v.784zm3.074 6.454h-.719v-2.771h.719v2.771zm-2.042-4.626h-.893v1.833h-.905v-4.755h1.798c.866 0 1.529.394 1.529 1.417 0 1.019-.684 1.505-1.529 1.505zm-.893-.67h.689c.422 0 .829-.157.829-.835 0-.446-.176-.804-.829-.804h-.689v1.639zm.956 1.476l.771 1.832h-.9l-.713-1.832h.842zm.146-3.521h-.927v-.785h2.56v.785h-.927v2.152h-.706v-2.152zm.872 5.703c0 .681-.581 1.354-1.516 1.354-.927 0-1.5-.673-1.5-1.354 0-.701.558-1.354 1.5-1.354.958 0 1.516.653 1.516 1.354zm-.75 0c0-.331-.295-.633-.756-.633-.45 0-.756.302-.756.633 0 .333.295.635.756.635.46-.001.756-.303.756-.635zm1.723 0c0 .681-.582 1.354-1.516 1.354-.928 0-1.5-.673-1.5-1.354 0-.701.556-1.354 1.5-1.354.958 0 1.516.653 1.516 1.354zm-.75 0c0-.331-.295-.633-.756-.633-.45 0-.756.302-.756.633 0 .333.295.635.756.635.46-.001.75