import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Create axios instance with default config
const backendApi = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
    timeout: 10000, // 10 seconds timeout
});

// Request interceptor
backendApi.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response interceptor
backendApi.interceptors.response.use(
    (response) => response,
    async (error) => {
        const originalRequest = error.config;

        // Handle token expiration
        if (error.response?.status === 401 && !originalRequest._retry) {
            originalRequest._retry = true;

            try {
                // Try to refresh the token
                const response = await backendApi.post('/auth/refresh');
                const { access_token } = response.data;
                
                // Update token in localStorage
                localStorage.setItem('token', access_token);
                
                // Retry the original request
                originalRequest.headers.Authorization = `Bearer ${access_token}`;
                return backendApi(originalRequest);
            } catch (refreshError) {
                // If refresh fails, clear tokens and redirect to login
                localStorage.removeItem('token');
                window.location.href = '/login';
                return Promise.reject(refreshError);
            }
        }

        // Handle other errors
        if (error.response) {
            // Server responded with error status
            const errorMessage = error.response.data?.error || 'An error occurred';
            return Promise.reject(new Error(errorMessage));
        } else if (error.request) {
            // Request was made but no response received
            return Promise.reject(new Error('Network error. Please check your connection.'));
        } else {
            // Something happened in setting up the request
            return Promise.reject(new Error('An unexpected error occurred.'));
        }
    }
);

// Auth functions
export const register = async (userData) => {
    try {
        const response = await backendApi.post('/auth/register', userData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const login = async (username, password) => {
    try {
        const response = await backendApi.post('/auth/login', { username, password });
        const { access_token, user } = response.data;
        
        // Store token
        localStorage.setItem('token', access_token);
        
        return user;
    } catch (error) {
        throw error;
    }
};

export const logout = () => {
    localStorage.removeItem('token');
};

export const getCurrentUser = async () => {
    try {
        const response = await backendApi.get('/auth/me');
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Content functions
export const getFeaturedContent = async () => {
    try {
        const response = await backendApi.get('/content/featured');
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const getContentById = async (contentId) => {
    try {
        const response = await backendApi.get(`/content/${contentId}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// User functions
export const getUserProfile = async () => {
    try {
        const response = await backendApi.get('/users/profile');
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const updateUserProfile = async (profileData) => {
    try {
        const response = await backendApi.put('/users/profile', profileData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Subscription functions
export const getSubscription = async () => {
    try {
        const response = await backendApi.get('/subscriptions/me');
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const updateSubscription = async (subscriptionData) => {
    try {
        const response = await backendApi.put('/subscriptions/me', subscriptionData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Admin functions
export const getDashboardStats = async () => {
    try {
        const response = await backendApi.get('/admin/stats');
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const getUsers = async () => {
    try {
        const response = await backendApi.get('/admin/users');
        return response.data;
    } catch (error) {
        throw error;
    }
};

export const updateUserRole = async (userId, role) => {
    try {
        const response = await backendApi.put(`/admin/users/${userId}/role`, { role });
        return response.data;
    } catch (error) {
        throw error;
    }
};

// User management
const getAllUsers = () => {
    return backendApi.get('/users');
};

const getUserById = (userId) => {
    return backendApi.get(`/users/${userId}`);
};

const updateUser = (userId, userData) => {
    return backendApi.put(`/users/${userId}`, userData);
};

const deleteUser = (userId) => {
    return backendApi.delete(`/users/${userId}`);
};

const createUser = (userData) => {
    return backendApi.post('/users', userData);
};

// Subscription management
const getAllSubscriptionPlans = () => {
    return backendApi.get('/subscriptions/plans');
};

const createSubscription = (planId) => {
    return backendApi.post('/subscriptions', { plan_id: planId });
};

const cancelSubscription = () => {
    return backendApi.delete('/subscriptions/me');
};

const checkSubscription = () => {
    return backendApi.get('/subscriptions/check');
};

// Access code management
const generateAccessCode = () => {
    return backendApi.post('/access-codes/generate');
};

const getAccessCodes = () => {
    return backendApi.get('/access-codes/me');
};

const redeemAccessCode = (code) => {
    return backendApi.post('/access-codes/redein', { code });
};

// Admin functions
const getAllSubscriptions = () => {
    return backendApi.get('/admin/subscriptions');
};

const getUsersWithRoles = () => {
    return backendApi.get('/admin/users');
};

// Watchlist/Favorites
const getFavorites = () => {
    return backendApi.get('/favorites');
};

const addToFavorites = (contentId, contentType) => {
    return backendApi.post('/favorites', { contentId, contentType });
};

const removeFromFavorites = (contentId) => {
    return backendApi.delete(`/favorites/${contentId}`);
};

// Watch History
const getWatchHistory = () => {
    return backendApi.get('/watch-history');
};

const addToWatchHistory = (contentId, watchDuration, watchPercentage) => {
    return backendApi.post('/watch-history', { contentId, watchDuration, watchPercentage });
};

// User Profiles
const getUserProfiles = () => {
    return backendApi.get('/profiles');
};

const createProfile = (profileData) => {
    return backendApi.post('/profiles', profileData);
};

const updateProfile = (profileId, profileData) => {
    return backendApi.put(`/profiles/${profileId}`, profileData);
};

const deleteProfile = (profileId) => {
    return backendApi.delete(`/profiles/${profileId}`);
};

export default backendApi;
