import React, { Suspense, lazy } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { LoadingProvider } from './context/LoadingContext';
import { NotificationProvider } from './context/NotificationContext';
import { useAuth } from './hooks/useAuth';

// Layout Components
import Navbar from './components/Layout/Navbar';
import Footer from './components/Layout/Footer';
import AdminSidebar from './components/Layout/AdminSidebar';
import LoadingSpinner from './components/UI/LoadingSpinner';

// Directly import AuthTest for debugging
import AuthTest from './debug/AuthTest';

// Lazy-loaded pages for better performance
const Home = lazy(() => import('./pages/Home'));
const Login = lazy(() => import('./pages/Login'));
const Register = lazy(() => import('./pages/Register'));
const ForgotPassword = lazy(() => import('./pages/ForgotPassword'));
const ResetPassword = lazy(() => import('./pages/ResetPassword'));
const Browse = lazy(() => import('./pages/Browse'));
const MovieDetails = lazy(() => import('./pages/MovieDetails'));
const TvShowDetails = lazy(() => import('./pages/TvShowDetails'));
const Profile = lazy(() => import('./pages/Profile'));
const Subscriptions = lazy(() => import('./pages/Subscriptions'));
const NotFound = lazy(() => import('./pages/NotFound'));

// Admin Pages
const AdminDashboard = lazy(() => import('./pages/Admin/Dashboard'));
const AdminUsers = lazy(() => import('./pages/Admin/Users'));
const AdminContent = lazy(() => import('./pages/Admin/Content'));
const AdminSubscriptions = lazy(() => import('./pages/Admin/Subscriptions'));
const AdminProfile = lazy(() => import('./pages/Admin/AdminProfile'));
const AuditLogs = lazy(() => import('./pages/Admin/AuditLogs'));

// Protected Routes Component
const ProtectedRoute = ({ children, requiredRoles = ['user', 'admin', 'superadmin'] }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner fullScreen message="Authenticating..." />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (!requiredRoles.includes(user.role)) {
    return <Navigate to="/browse" />;
  }

  return children;
};

// Subscription Check Route
const SubscriptionRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner fullScreen message="Checking subscription..." />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (!user.hasSubscription && !user.hasAccessCode) {
    return <Navigate to="/subscriptions" />;
  }

  return children;
};

// Admin Route Component
const AdminRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <LoadingSpinner fullScreen message="Authenticating admin..." />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (user.role !== 'admin' && user.role !== 'superadmin') {
    return <Navigate to="/browse" />;
  }

  return (
    <div className="flex h-screen">
      <AdminSidebar />
      <div className="flex-1 overflow-auto ml-64">
        {children}
      </div>
    </div>
  );
};

// App Wrapper Component - Provides context to all routes
const AppWrapper = () => {
  return (
    <AuthProvider>
      <LoadingProvider>
        <NotificationProvider>
          <AppContent />
        </NotificationProvider>
      </LoadingProvider>
    </AuthProvider>
  );
};

// Main App Content Component
const AppContent = () => {
  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Navbar />
      <main className="flex-grow">
        <Suspense fallback={<LoadingSpinner fullScreen message="Loading content..." />}>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/reset-password" element={<ResetPassword />} />

            {/* Protected Routes */}
            <Route
              path="/browse"
              element={
                <ProtectedRoute>
                  <Browse />
                </ProtectedRoute>
              }
            />
            <Route
              path="/movie/:id"
              element={
                <SubscriptionRoute>
                  <MovieDetails />
                </SubscriptionRoute>
              }
            />
            <Route
              path="/tv/:id"
              element={
                <SubscriptionRoute>
                  <TvShowDetails />
                </SubscriptionRoute>
              }
            />
            <Route
              path="/profile"
              element={
                <ProtectedRoute>
                  <Profile />
                </ProtectedRoute>
              }
            />
            <Route path="/subscriptions" element={<Subscriptions />} />

            {/* Admin Routes */}
            <Route
              path="/admin"
              element={
                <AdminRoute>
                  <AdminDashboard />
                </AdminRoute>
              }
            />
            <Route
              path="/admin/profile"
              element={
                <AdminRoute>
                  <AdminProfile />
                </AdminRoute>
              }
            />
            <Route
              path="/admin/users"
              element={
                <AdminRoute>
                  <AdminUsers />
                </AdminRoute>
              }
            />
            <Route
              path="/admin/content"
              element={
                <AdminRoute>
                  <AdminContent />
                </AdminRoute>
              }
            />
            <Route
              path="/admin/subscriptions"
              element={
                <AdminRoute>
                  <AdminSubscriptions />
                </AdminRoute>
              }
            />
            <Route
              path="/admin/logs"
              element={
                <AdminRoute>
                  <AuditLogs />
                </AdminRoute>
              }
            />

            {/* 404 Route */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </Suspense>
      </main>
      <Footer />
    </div>
  );
};

export default AppWrapper;
