import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [scrolled, setScrolled] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const isBrowsePage = location.pathname === '/browse';

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    // Add event listener
    window.addEventListener('scroll', handleScroll);
    
    // Clean up event listener
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/browse?search=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  return (
    <nav 
      className={`${
        scrolled 
          ? 'bg-gray-900 bg-opacity-95 backdrop-blur-sm shadow-lg py-2 h-14' 
          : 'bg-gray-900 py-3 h-14'
      } text-white fixed top-0 left-0 right-0 z-50 transition-all duration-300`}
    >
      <div className="container mx-auto px-4 flex justify-between items-center h-full">
        <div className="flex items-center">
          <Link 
            to={isAuthenticated ? "/browse" : "/"} 
            className={`${
              scrolled ? 'text-xl' : 'text-2xl'
            } font-bold text-red-600 transition-all duration-300 hover:text-red-500 flex items-center`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
            </svg>
            VortexTV
          </Link>
          
          {isAuthenticated && (
            <div className="ml-10 space-x-6 hidden md:flex">
              <Link 
                to="/browse" 
                className={`hover:text-red-500 transition-colors ${location.pathname === '/browse' ? 'text-red-500 font-medium' : ''}`}
              >
                Browse
              </Link>
              {!(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link 
                  to="/subscriptions" 
                  className={`hover:text-red-500 transition-colors ${location.pathname === '/subscriptions' ? 'text-red-500 font-medium' : ''}`}
                >
                  Subscriptions
                </Link>
              )}
              {(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link 
                  to="/admin" 
                  className={`hover:text-red-500 transition-colors ${location.pathname.startsWith('/admin') ? 'text-red-500 font-medium' : ''}`}
                >
                  Admin Panel
                </Link>
              )}
            </div>
          )}
        </div>
        
        <div className="flex items-center space-x-4">
          {/* Search bar - only visible on browse page */}
          {isAuthenticated && isBrowsePage && (
            <form onSubmit={handleSearch} className="relative mr-2 hidden sm:block">
              <input
                type="text"
                placeholder="Search movies & shows..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="bg-gray-800 text-white px-4 py-1 rounded-full w-48 lg:w-64 focus:outline-none focus:ring-2 focus:ring-red-500 text-sm"
              />
              <button 
                type="submit" 
                className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-red-500"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </button>
            </form>
          )}

          {isAuthenticated ? (
            <div className="flex items-center space-x-4">
              <Link 
                to="/profile" 
                className="hover:text-red-500 transition-colors flex items-center"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                </svg>
                <span className="hidden sm:inline">Profile</span>
              </Link>
              <button 
                onClick={logout}
                className="bg-red-600 hover:bg-red-700 px-4 py-1.5 rounded-full transition-colors text-sm font-medium flex items-center"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                </svg>
                <span className="hidden sm:inline">Logout</span>
              </button>
            </div>
          ) : (
            <div className="flex items-center space-x-4">
              <Link to="/login" className="hover:text-red-500 transition-colors">
                Login
              </Link>
              <Link to="/register" className="bg-red-600 hover:bg-red-700 px-4 py-1.5 rounded-full transition-colors text-sm font-medium">
                Register
              </Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
