import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { getUserProfile, getSubscription } from '../api/backendApi';
import { motion } from 'framer-motion';
import { FaUser, FaEnvelope, FaCrown, FaCalendarAlt, FaEdit, FaSave, FaTimes, FaClipboardCheck, FaShieldAlt, FaTicketAlt, FaCreditCard } from 'react-icons/fa';

// No need to import index.css as it's already imported in index.js at the app root

// Helper function to ensure we always have valid data
const ensureObject = (obj, fallback = {}) => {
  return obj && typeof obj === 'object' ? obj : fallback;
};

const ensureArray = (arr, fallback = []) => {
  return Array.isArray(arr) ? arr : fallback;
};

const Profile = () => {
  const { user, isAuthenticated } = useAuth();
  const [profile, setProfile] = useState({
    username: '',
    email: '',
    role_name: '',
    created_at: new Date().toISOString() // Fallback date
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subscriptionData, setSubscriptionData] = useState(null);
  const [accessCodes, setAccessCodes] = useState([]);
  const [newAccessCode, setNewAccessCode] = useState(null);
  const [showGenCodeConfirm, setShowGenCodeConfirm] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    } else {
      fetchData();
    }
  }, [isAuthenticated, navigate]);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch profile data first
      try {
        const profileData = await getUserProfile();
        if (profileData) {
          setProfile(ensureObject(profileData, {
            username: user?.username || 'User',
            email: user?.email || 'N/A',
            role_name: user?.role || 'user',
            created_at: new Date().toISOString()
          }));
          
          setFormData({
            username: profileData.username || user?.username || '',
            email: profileData.email || user?.email || ''
          });
        }
      } catch (profileErr) {
        console.error('Error fetching profile:', profileErr);
        // Fallback to user data from AuthContext
        if (user) {
          setProfile({
            username: user.username || 'User',
            email: user.email || 'N/A',
            role_name: user.role || 'user',
            created_at: new Date().toISOString()
          });
          
          setFormData({
            username: user.username || '',
            email: user.email || ''
          });
        }
      }
      
      // Fetch subscription data
      try {
        const subData = await getSubscription();
        if (subData) {
          setSubscriptionData(subData);
          setAccessCodes(ensureArray(subData.access_codes, []));
        }
      } catch (subErr) {
        console.error('Error fetching subscription:', subErr);
        // Subscription is optional, so we don't set an error
      }
      
    } catch (err) {
      setError('Failed to load profile information');
      console.error('Error fetching profile data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    
    try {
      // Using backendApi but need to implement this function
      // await updateUserProfile(formData);
      
      // For now, just update the local state to simulate success
      setProfile({
        ...profile,
        username: formData.username,
        email: formData.email
      });
      
      setEditMode(false);
      // fetchData(); // Uncomment when API is ready
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
      console.error('Error updating profile:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGenerateAccessCode = async () => {
    try {
      // Using backendApi but need to implement this function
      // const code = await generateAccessCode();
      // setNewAccessCode(code);
      setNewAccessCode("SAMPLE-CODE-123"); // Placeholder
      // fetchData(); // Uncomment when API is ready
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to generate access code');
      console.error('Error generating access code:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="mb-8"
        >
          <h1 className="text-3xl font-bold text-gray-900">My Profile</h1>
          <p className="mt-2 text-sm text-gray-600">
            Manage your account information and subscription details
          </p>
        </motion.div>
        
        {error && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-red-50 border-l-4 border-red-500 p-4 mb-6 rounded-md shadow-sm"
          >
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            </div>
          </motion.div>
        )}
        
        {newAccessCode && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-green-50 border-l-4 border-green-500 p-4 mb-6 rounded-md shadow-sm"
          >
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-green-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-green-700 font-medium">Access code generated successfully!</p>
                <p className="text-sm text-green-700 mt-1">Share this code with a friend: <span className="bg-white px-2 py-1 rounded font-mono text-green-800">{newAccessCode}</span></p>
                <button 
                  onClick={() => setNewAccessCode(null)} 
                  className="text-xs text-green-600 underline mt-2 hover:text-green-800"
                >
                  Dismiss
                </button>
              </div>
            </div>
          </motion.div>
        )}
        
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Profile Information */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="lg:col-span-2"
          >
            <div className="bg-white shadow rounded-xl overflow-hidden">
              <div className="px-6 py-5 border-b border-gray-200 flex justify-between items-center">
                <h2 className="text-xl font-semibold text-gray-800">Account Information</h2>
                {!editMode && (
                  <button
                    onClick={() => setEditMode(true)}
                    className="flex items-center text-sm text-blue-600 hover:text-blue-800 focus:outline-none"
                  >
                    <FaEdit className="mr-1" />
                    Edit
                  </button>
                )}
              </div>
              
              <div className="p-6">
                {editMode ? (
                  <form onSubmit={handleUpdateProfile} className="space-y-6">
                    <div>
                      <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                        Username
                      </label>
                      <div className="relative rounded-md shadow-sm">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <FaUser className="h-5 w-5 text-gray-400" />
                        </div>
                        <input
                          id="username"
                          name="username"
                          type="text"
                          value={formData.username}
                          onChange={handleInputChange}
                          className="pl-10 block w-full rounded-md focus:ring-blue-500 focus:border-blue-500 transition-colors py-2 px-3 border border-gray-300 text-gray-800 bg-white"
                        />
                      </div>
                    </div>
                    
                    <div>
                      <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                        Email
                      </label>
                      <div className="relative rounded-md shadow-sm">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <FaEnvelope className="h-5 w-5 text-gray-400" />
                        </div>
                        <input
                          id="email"
                          name="email"
                          type="email"
                          value={formData.email}
                          onChange={handleInputChange}
                          className="pl-10 block w-full rounded-md focus:ring-blue-500 focus:border-blue-500 transition-colors py-2 px-3 border border-gray-300 text-gray-800 bg-white"
                        />
                      </div>
                    </div>
                    
                    <div className="flex justify-end space-x-3">
                      <button
                        type="button"
                        onClick={() => setEditMode(false)}
                        className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md shadow-sm text-gray-700 bg-white hover:bg-gray-50 focus:outline-none"
                      >
                        <FaTimes className="mr-2" />
                        Cancel
                      </button>
                      <button
                        type="submit"
                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none"
                      >
                        <FaSave className="mr-2" />
                        Save Changes
                      </button>
                    </div>
                  </form>
                ) : (
                  <div className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div className="flex items-center space-x-3">
                        <div className="flex-shrink-0">
                          <div className="bg-blue-100 rounded-full p-2">
                            <FaUser className="h-5 w-5 text-blue-600" />
                          </div>
                        </div>
                        <div>
                          <div className="text-xs font-medium text-gray-500 uppercase tracking-wider">Username</div>
                          <div className="mt-1 text-gray-900 font-medium">{profile?.username || 'User'}</div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <div className="flex-shrink-0">
                          <div className="bg-green-100 rounded-full p-2">
                            <FaEnvelope className="h-5 w-5 text-green-600" />
                          </div>
                        </div>
                        <div>
                          <div className="text-xs font-medium text-gray-500 uppercase tracking-wider">Email</div>
                          <div className="mt-1 text-gray-900 font-medium">{profile?.email || 'N/A'}</div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <div className="flex-shrink-0">
                          <div className="bg-purple-100 rounded-full p-2">
                            <FaShieldAlt className="h-5 w-5 text-purple-600" />
                          </div>
                        </div>
                        <div>
                          <div className="text-xs font-medium text-gray-500 uppercase tracking-wider">Role</div>
                          <div className="mt-1 text-gray-900 font-medium capitalize">{profile?.role_name || user?.role || 'User'}</div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <div className="flex-shrink-0">
                          <div className="bg-yellow-100 rounded-full p-2">
                            <FaCalendarAlt className="h-5 w-5 text-yellow-600" />
                          </div>
                        </div>
                        <div>
                          <div className="text-xs font-medium text-gray-500 uppercase tracking-wider">Joined</div>
                          <div className="mt-1 text-gray-900 font-medium">
                            {profile?.created_at 
                              ? new Date(profile.created_at).toLocaleDateString() 
                              : new Date().toLocaleDateString()}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </motion.div>
          
          {/* Subscription Card */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            <div className="bg-white shadow rounded-xl overflow-hidden h-full">
              <div className="px-6 py-5 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-gray-800">Subscription</h2>
              </div>
              
              <div className="p-6 flex flex-col h-full">
                {subscriptionData ? (
                  <div className="space-y-6 flex-grow">
                    <div className="flex items-center justify-center mb-4">
                      <div className="bg-blue-100 rounded-full p-3">
                        <FaCrown className="h-8 w-8 text-blue-600" />
                      </div>
                    </div>
                    
                    <div className="text-center">
                      <h3 className="text-lg font-semibold text-gray-900">{subscriptionData.plan_name || "Premium Plan"}</h3>
                      <div className="mt-1 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                        {subscriptionData.is_active ? "Active" : "Inactive"}
                      </div>
                    </div>
                    
                    <div className="border-t border-gray-200 pt-4">
                      <dl className="divide-y divide-gray-200">
                        <div className="py-3 flex justify-between">
                          <dt className="text-sm font-medium text-gray-500">Start Date</dt>
                          <dd className="text-sm text-right text-gray-900">
                            {subscriptionData.start_date 
                              ? new Date(subscriptionData.start_date).toLocaleDateString() 
                              : 'N/A'}
                          </dd>
                        </div>
                        <div className="py-3 flex justify-between">
                          <dt className="text-sm font-medium text-gray-500">Next Billing</dt>
                          <dd className="text-sm text-right text-gray-900">
                            {subscriptionData.next_billing_date 
                              ? new Date(subscriptionData.next_billing_date).toLocaleDateString() 
                              : 'N/A'}
                          </dd>
                        </div>
                        <div className="py-3 flex justify-between">
                          <dt className="text-sm font-medium text-gray-500">Monthly Cost</dt>
                          <dd className="text-sm font-medium text-right text-gray-900">
                            ${subscriptionData.price || "14.99"}
                          </dd>
                        </div>
                      </dl>
                    </div>
                    
                    <div className="mt-auto pt-4">
                      <button className="w-full inline-flex justify-center items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none">
                        <FaCreditCard className="mr-2" />
                        Manage Billing
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="flex flex-col items-center justify-center h-full text-center py-8">
                    <div className="bg-gray-100 rounded-full p-3 mb-4">
                      <FaTicketAlt className="h-8 w-8 text-gray-500" />
                    </div>
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No Active Subscription</h3>
                    <p className="text-gray-500 text-sm mb-6">Subscribe to enjoy premium content and exclusive features</p>
                    <button 
                      onClick={() => navigate('/subscriptions')}
                      className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none"
                    >
                      View Plans
                    </button>
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        </div>
        
        {/* Access Codes Section - Only show if user has subscription or this is an admin */}
        {(subscriptionData || profile?.role_name === 'admin' || user?.role === 'admin') && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="mt-6"
          >
            <div className="bg-white shadow rounded-xl overflow-hidden">
              <div className="px-6 py-5 border-b border-gray-200 flex justify-between items-center">
                <h2 className="text-xl font-semibold text-gray-800">Access Codes</h2>
                {!showGenCodeConfirm && (
                  <button
                    onClick={() => setShowGenCodeConfirm(true)}
                    className="inline-flex items-center px-3 py-1.5 border border-transparent text-sm font-medium rounded-md text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none"
                  >
                    <FaClipboardCheck className="mr-1.5 h-4 w-4" />
                    Generate Code
                  </button>
                )}
              </div>
              
              <div className="p-6">
                {showGenCodeConfirm ? (
                  <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                    <div className="flex">
                      <div className="flex-shrink-0">
                        <svg className="h-5 w-5 text-blue-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                        </svg>
                      </div>
                      <div className="ml-3">
                        <h3 className="text-sm font-medium text-blue-800">Generate a new access code?</h3>
                        <div className="mt-2 text-sm text-blue-700">
                          <p>This will create a code that can be used by a friend to access premium content for 7 days.</p>
                        </div>
                        <div className="mt-4 flex space-x-3">
                          <button
                            type="button"
                            onClick={() => {
                              handleGenerateAccessCode();
                              setShowGenCodeConfirm(false);
                            }}
                            className="inline-flex items-center px-3 py-1.5 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none"
                          >
                            Generate
                          </button>
                          <button
                            type="button"
                            onClick={() => setShowGenCodeConfirm(false)}
                            className="inline-flex items-center px-3 py-1.5 border border-gray-300 text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none"
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : accessCodes && accessCodes.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Code
                          </th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Created
                          </th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                            Status
                          </th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {accessCodes.map((code, index) => (
                          <tr key={index}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 font-mono">
                              {code.code || `CODE-${index + 1}`}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {code.created_at
                                ? new Date(code.created_at).toLocaleDateString()
                                : new Date().toLocaleDateString()}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              {code.is_used ? (
                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                                  Used
                                </span>
                              ) : (
                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                  Available
                                </span>
                              )}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <div className="text-center py-6">
                    <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                    </svg>
                    <h3 className="mt-2 text-sm font-medium text-gray-900">No access codes</h3>
                    <p className="mt-1 text-sm text-gray-500">Generate a code to share premium access with friends.</p>
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Profile;
