import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState({
    userStats: {
      totalUsers: 0,
      activeUsers: 0,
      roleDistribution: []
    },
    subscriptionStats: {
      totalSubscriptions: 0,
      activeSubscriptions: 0,
      revenueThisMonth: 0
    },
    planDistribution: [],
    accessCodeStats: {
      totalCodes: 0,
      usedCodes: 0
    },
    recentUsers: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/admin/stats');
        if (response.data && typeof response.data === 'object') {
          setStats(prevStats => ({
            ...prevStats,
            ...response.data,
            userStats: {
              ...prevStats.userStats,
              ...(response.data.userStats || {})
            },
            subscriptionStats: {
              ...prevStats.subscriptionStats,
              ...(response.data.subscriptionStats || {})
            },
            accessCodeStats: {
              ...prevStats.accessCodeStats,
              ...(response.data.accessCodeStats || {})
            },
            planDistribution: response.data.planDistribution || [],
            recentUsers: response.data.recentUsers || []
          }));
        }
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-full">
        <div className="text-xl">Loading dashboard data...</div>
      </div>
    );
  }

  // Calculate percentages safely
  const activeUsersPercentage = stats.userStats.totalUsers > 0 
    ? Math.round((stats.userStats.activeUsers / stats.userStats.totalUsers) * 100)
    : 0;
  
  const activeSubscriptionsPercentage = stats.subscriptionStats.totalSubscriptions > 0
    ? Math.round((stats.subscriptionStats.activeSubscriptions / stats.subscriptionStats.totalSubscriptions) * 100)
    : 0;
  
  const usedCodesPercentage = stats.accessCodeStats.totalCodes > 0
    ? Math.round((stats.accessCodeStats.usedCodes / stats.accessCodeStats.totalCodes) * 100)
    : 0;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Admin Dashboard</h1>
      
      {error && (
        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {/* User Stats Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium">Users</h2>
            <span className="p-2 bg-blue-100 text-blue-600 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
              </svg>
            </span>
          </div>
          <div className="text-3xl font-bold mb-2">{stats.userStats.totalUsers}</div>
          <div className="text-sm text-gray-500">
            {stats.userStats.activeUsers} active ({activeUsersPercentage}%)
          </div>
        </div>
        
        {/* Subscription Stats Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium">Subscriptions</h2>
            <span className="p-2 bg-green-100 text-green-600 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M4 4a2 2 0 00-2 2v4a2 2 0 002 2V6h10a2 2 0 00-2-2H4zm2 6a2 2 0 012-2h8a2 2 0 012 2v4a2 2 0 01-2 2H8a2 2 0 01-2-2v-4zm6 4a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
              </svg>
            </span>
          </div>
          <div className="text-3xl font-bold mb-2">{stats.subscriptionStats.totalSubscriptions}</div>
          <div className="text-sm text-gray-500">
            {stats.subscriptionStats.activeSubscriptions} active ({activeSubscriptionsPercentage}%)
          </div>
        </div>
        
        {/* Monthly Revenue Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium">Monthly Revenue</h2>
            <span className="p-2 bg-purple-100 text-purple-600 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M4 4a2 2 0 00-2 2v4a2 2 0 002 2V6h10a2 2 0 00-2-2H4zm2 6a2 2 0 012-2h8a2 2 0 012 2v4a2 2 0 01-2 2H8a2 2 0 01-2-2v-4zm6 4a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
              </svg>
            </span>
          </div>
          <div className="text-3xl font-bold mb-2">${stats.subscriptionStats.revenueThisMonth.toFixed(2)}</div>
          <div className="text-sm text-gray-500">
            From {stats.subscriptionStats.activeSubscriptions} active subscriptions
          </div>
        </div>
        
        {/* Access Codes Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium">Access Codes</h2>
            <span className="p-2 bg-yellow-100 text-yellow-600 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M18 8a6 6 0 01-7.743 5.743L10 14l-1 1-1 1H6v-1l1-1 1-1-1.257-1.257A6 6 0 1118 8zm-6-4a1 1 0 10-2 0 2 2 0 012 0z" clipRule="evenodd" />
              </svg>
            </span>
          </div>
          <div className="text-3xl font-bold mb-2">{stats.accessCodeStats.totalCodes}</div>
          <div className="text-sm text-gray-500">
            {stats.accessCodeStats.usedCodes} used ({usedCodesPercentage}%)
          </div>
        </div>
      </div>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Plan Distribution */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-lg font-medium mb-4">Subscription Plan Distribution</h2>
          <div className="space-y-4">
            {stats.planDistribution.map((plan) => (
              <div key={plan.plan_name}>
                <div className="flex justify-between mb-1">
                  <span>{plan.plan_name}</span>
                  <span className="font-medium">{plan.count}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div
                    className="bg-red-600 h-2.5 rounded-full"
                    style={{ width: `${(plan.count / (stats.subscriptionStats.totalSubscriptions || 1)) * 100}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        {/* Recent Users */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-lg font-medium mb-4">Recent User Registrations</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    User
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Role
                  </th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Registered
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {stats.recentUsers.map((user) => (
                  <tr key={user.user_id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div>
                          <div className="text-sm font-medium text-gray-900">{user.username}</div>
                          <div className="text-sm text-gray-500">{user.email}</div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                        {user.role_name}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {new Date(user.created_at).toLocaleDateString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
