import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const AdminSidebar = () => {
  const { user } = useAuth();
  const location = useLocation();
  const [isCollapsed, setIsCollapsed] = useState(false);
  
  // Check if current path is active
  const isActive = (path) => {
    return location.pathname === path;
  };
  
  // Toggle sidebar collapsed state
  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };
  
  return (
    <div 
      className={`bg-gray-900 text-white h-screen fixed left-0 top-0 overflow-y-auto pt-20 transition-all duration-300 ease-in-out ${
        isCollapsed ? 'w-16' : 'w-64'
      }`}
    >
      {/* Toggle button */}
      <button 
        onClick={toggleSidebar}
        className="absolute top-5 right-5 bg-gray-800 hover:bg-gray-700 rounded-full p-2 text-white transition-all duration-200"
        aria-label={isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
      >
        {isCollapsed ? (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
          </svg>
        ) : (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
          </svg>
        )}
      </button>
      
      <div className={`px-4 py-4 ${isCollapsed ? 'text-center' : ''}`}>
        {!isCollapsed && <h2 className="text-xl font-bold text-red-600 mb-6">Admin Panel</h2>}
        
        {!isCollapsed && (
          <div className="mb-6">
            <div className="text-gray-400 mb-2 text-sm">Logged in as:</div>
            <div className="font-medium truncate">{user?.username}</div>
            <div className="text-sm text-gray-400">{user?.role}</div>
          </div>
        )}
        
        <nav className="space-y-2">
          <Link
            to="/admin"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-all duration-200 ${isCollapsed ? 'justify-center' : ''}`}
            title="Dashboard"
          >
            <span className={isCollapsed ? '' : 'mr-3'}>📊</span>
            {!isCollapsed && <span>Dashboard</span>}
          </Link>
          
          <Link
            to="/admin/users"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/users') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-all duration-200 ${isCollapsed ? 'justify-center' : ''}`}
            title="Users"
          >
            <span className={isCollapsed ? '' : 'mr-3'}>👥</span>
            {!isCollapsed && <span>Users</span>}
          </Link>
          
          <Link
            to="/admin/content"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/content') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-all duration-200 ${isCollapsed ? 'justify-center' : ''}`}
            title="Content"
          >
            <span className={isCollapsed ? '' : 'mr-3'}>🎬</span>
            {!isCollapsed && <span>Content</span>}
          </Link>
          
          <Link
            to="/admin/subscriptions"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/subscriptions') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-all duration-200 ${isCollapsed ? 'justify-center' : ''}`}
            title="Subscriptions"
          >
            <span className={isCollapsed ? '' : 'mr-3'}>💳</span>
            {!isCollapsed && <span>Subscriptions</span>}
          </Link>
        </nav>
      </div>
      
      <div className={`px-4 py-4 mt-auto ${isCollapsed ? 'text-center' : ''}`}>
        <Link
          to="/browse"
          className={`flex items-center py-3 px-4 text-gray-300 hover:bg-gray-800 hover:text-white rounded-md transition-all duration-200 ${isCollapsed ? 'justify-center' : ''}`}
          title="Back to Site"
        >
          <span className={isCollapsed ? '' : 'mr-3'}>🏠</span>
          {!isCollapsed && <span>Back to Site</span>}
        </Link>
      </div>
    </div>
  );
};

export default AdminSidebar;
