import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [scrolled, setScrolled] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [searchFocused, setSearchFocused] = useState(false);
  const isBrowsePage = location.pathname === '/browse';

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    // Add event listener
    window.addEventListener('scroll', handleScroll);
    
    // Clean up event listener
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setMobileMenuOpen(false);
  }, [location.pathname]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/browse?search=${encodeURIComponent(searchQuery.trim())}`);
      setSearchFocused(false);
    }
  };

  return (
    <>
      <nav 
        className={`${
          scrolled 
            ? 'bg-gray-900/85 backdrop-blur-lg shadow-lg py-2' 
            : 'bg-gradient-to-r from-gray-900 to-gray-800 py-3'
        } text-white fixed top-0 left-0 right-0 z-50 transition-all duration-300`}
      >
        <div className="container mx-auto px-4 flex justify-between items-center h-14">
          <div className="flex items-center">
            <Link 
              to={isAuthenticated ? "/browse" : "/"} 
              className={`${
                scrolled ? 'text-xl' : 'text-2xl'
              } font-bold text-transparent bg-clip-text bg-gradient-to-r from-red-500 to-red-600 transition-all duration-300 flex items-center group`}
            >
              <div className="transform group-hover:scale-110 group-hover:rotate-3 transition-transform duration-300">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-7 w-7 mr-2 text-red-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                </svg>
              </div>
              <span className="tracking-tight">VortexTV</span>
            </Link>
            
            {isAuthenticated && (
              <div className="ml-10 space-x-8 hidden md:flex">
                <NavLink to="/browse" current={location.pathname === '/browse'}>
                  Browse
                </NavLink>
                {!(user?.role === 'admin' || user?.role === 'superadmin') && (
                  <NavLink to="/subscriptions" current={location.pathname === '/subscriptions'}>
                    Subscriptions
                  </NavLink>
                )}
                {(user?.role === 'admin' || user?.role === 'superadmin') && (
                  <NavLink to="/admin" current={location.pathname.startsWith('/admin')}>
                    Admin Panel
                  </NavLink>
                )}
              </div>
            )}
          </div>
          
          <div className="flex items-center space-x-5">
            {/* Search bar - only visible on browse page */}
            {isAuthenticated && isBrowsePage && (
              <form 
                onSubmit={handleSearch} 
                className={`relative hidden sm:block transition-all duration-300 ${
                  searchFocused ? 'w-72 md:w-80' : 'w-48 md:w-64'
                }`}
              >
                <input
                  type="text"
                  placeholder="Search movies & shows..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onFocus={() => setSearchFocused(true)}
                  onBlur={() => setSearchFocused(false)}
                  className={`bg-gray-800/70 text-white px-4 py-2 pr-10 rounded-full w-full focus:outline-none focus:ring-2 transition-all duration-300 ${
                    searchFocused 
                      ? 'focus:ring-red-500 bg-gray-800/90 shadow-md' 
                      : 'focus:ring-gray-700'
                  } text-sm placeholder-gray-400`}
                />
                <button 
                  type="submit" 
                  className={`absolute right-3 top-1/2 transform -translate-y-1/2 transition-colors duration-300 ${
                    searchQuery.trim() ? 'text-red-500' : 'text-gray-400'
                  } hover:text-red-500`}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </form>
            )}

            {isAuthenticated ? (
              <div className="flex items-center space-x-5">
                <Link 
                  to="/profile" 
                  className="group relative flex items-center text-gray-300 hover:text-white transition-colors duration-300"
                >
                  <div className="flex items-center">
                    <div className="bg-gradient-to-br from-red-500 to-red-700 rounded-full p-1.5 mr-2 shadow-sm transition-transform duration-300 transform group-hover:scale-110">
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-white" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <span className="hidden sm:inline font-medium">Profile</span>
                  </div>
                  <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-red-500 group-hover:w-full transition-all duration-300"></span>
                </Link>
                <button 
                  onClick={logout}
                  className="flex items-center bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 px-5 py-2 rounded-full transition-all duration-300 text-sm font-medium shadow-md hover:shadow-lg transform hover:translate-y-[-1px]"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1.5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                  </svg>
                  <span className="hidden sm:inline">Logout</span>
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-5">
                <Link to="/login" className="relative group text-gray-300 font-medium transition-colors duration-300 hover:text-white">
                  Login
                  <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-red-500 group-hover:w-full transition-all duration-300"></span>
                </Link>
                <Link to="/register" className="bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 px-5 py-2 rounded-full transition-all duration-300 text-sm font-medium shadow-md hover:shadow-lg transform hover:translate-y-[-1px]">
                  Register
                </Link>
              </div>
            )}
            
            {/* Mobile menu button */}
            <button 
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              className="md:hidden flex items-center"
              aria-label="Toggle menu"
            >
              <div className="w-6 h-6 flex flex-col justify-between items-center relative">
                <span className={`bg-white block h-0.5 w-full rounded-sm transition-all duration-300 ${mobileMenuOpen ? 'rotate-45 translate-y-2.5' : ''}`}></span>
                <span className={`bg-white block h-0.5 w-full rounded-sm transition-all duration-300 ${mobileMenuOpen ? 'opacity-0' : 'opacity-100'}`}></span>
                <span className={`bg-white block h-0.5 w-full rounded-sm transition-all duration-300 ${mobileMenuOpen ? '-rotate-45 -translate-y-2.5' : ''}`}></span>
              </div>
            </button>
          </div>
        </div>
      </nav>
      
      {/* Mobile menu dropdown */}
      <div 
        className={`fixed inset-x-0 top-14 z-40 bg-gray-900/95 backdrop-blur-lg transform transition-transform duration-300 shadow-lg ${
          mobileMenuOpen ? 'translate-y-0' : '-translate-y-full'
        } md:hidden`}
      >
        <div className="container mx-auto px-4 py-3">
          {isAuthenticated && (
            <div className="flex flex-col space-y-4 border-b border-gray-700 pb-4">
              <Link 
                to="/browse" 
                className={`px-3 py-2 rounded-lg transition-colors ${location.pathname === '/browse' ? 'bg-red-600/20 text-white font-medium' : 'text-gray-300 hover:bg-gray-800'}`}
              >
                Browse
              </Link>
              {!(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link 
                  to="/subscriptions" 
                  className={`px-3 py-2 rounded-lg transition-colors ${location.pathname === '/subscriptions' ? 'bg-red-600/20 text-white font-medium' : 'text-gray-300 hover:bg-gray-800'}`}
                >
                  Subscriptions
                </Link>
              )}
              {(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link 
                  to="/admin" 
                  className={`px-3 py-2 rounded-lg transition-colors ${location.pathname.startsWith('/admin') ? 'bg-red-600/20 text-white font-medium' : 'text-gray-300 hover:bg-gray-800'}`}
                >
                  Admin Panel
                </Link>
              )}
            </div>
          )}
          
          {isAuthenticated && isBrowsePage && (
            <div className="py-3 border-b border-gray-700">
              <form onSubmit={handleSearch} className="relative">
                <input
                  type="text"
                  placeholder="Search movies & shows..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="bg-gray-800 text-white px-4 py-2 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-red-500 text-sm"
                />
                <button 
                  type="submit" 
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-red-500"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </form>
            </div>
          )}
          
          <div className="pt-3 flex flex-col space-y-4">
            {isAuthenticated ? (
              <>
                <Link 
                  to="/profile" 
                  className="flex items-center px-3 py-2 text-gray-300 hover:bg-gray-800 rounded-lg transition-colors"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3 text-red-500" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                  </svg>
                  My Profile
                </Link>
                <button 
                  onClick={logout}
                  className="flex items-center px-3 py-2 text-gray-300 hover:bg-gray-800 rounded-lg transition-colors text-left"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3 text-red-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                  </svg>
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link 
                  to="/login" 
                  className="flex items-center px-3 py-2 text-gray-300 hover:bg-gray-800 rounded-lg transition-colors"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3 text-red-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
                  </svg>
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="flex items-center px-3 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18 9v3m0 0v3m0-3h3m-3 0h-3m-2-5a4 4 0 11-8 0 4 4 0 018 0zM3 20a6 6 0 0112 0v1H3v-1z" />
                  </svg>
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

// Helper component for navigation links
const NavLink = ({ to, current, children }) => {
  return (
    <Link 
      to={to} 
      className={`relative group ${current ? 'text-white font-medium' : 'text-gray-300 hover:text-white'} transition-colors duration-300`}
    >
      <span className="tracking-wide">{children}</span>
      <span 
        className={`absolute -bottom-1 left-0 h-0.5 bg-red-500 transition-all duration-300 ${
          current ? 'w-full' : 'w-0 group-hover:w-full'
        }`}
      ></span>
    </Link>
  );
};

export default Navbar;
