import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { setupTokenRefresh, getCurrentUser, getUserSubscription, updateUser, generateAccessCode, redeemAccessCode, updatePassword } from '../api/backendApi';

const Profile = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [subscriptionData, setSubscriptionData] = useState(null);
  const [accessCodes, setAccessCodes] = useState([]);
  const [newAccessCode, setNewAccessCode] = useState(null);
  const [showGenCodeConfirm, setShowGenCodeConfirm] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [showPasswordForm, setShowPasswordForm] = useState(false);
  const [accessCodeInput, setAccessCodeInput] = useState('');
  const [isRedeemingCode, setIsRedeemingCode] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: ''
  });
  const [passwordData, setPasswordData] = useState({
    current_password: '',
    new_password: '',
    confirm_password: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    } else if (user && (user.role === 'admin' || user.role === 'superadmin')) {
      // Redirect admin users to the admin profile
      navigate('/admin/profile');
    } else {
      setupTokenRefresh();
      fetchProfile();
      fetchSubscription();
    }
  }, [isAuthenticated, navigate, user]);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const response = await getCurrentUser();
      setProfile(response.data);
      setFormData({
        username: response.data.username,
        email: response.data.email
      });
    } catch (err) {
      setError('Failed to load profile information');
      console.error('Error fetching profile:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchSubscription = async () => {
    try {
      const response = await getUserSubscription();
      setSubscriptionData(response.data);
      setAccessCodes(response.data.access_codes || []);
    } catch (err) {
      // Only log error, don't set error state since subscription might not exist
      console.error('Error fetching subscription:', err);
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();

    try {
      await updateUser(user.user_id, formData);
      setEditMode(false);
      setSuccessMessage('Profile updated successfully');
      fetchProfile();

      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
      console.error('Error updating profile:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGenerateAccessCode = async () => {
    try {
      setIsRedeemingCode(true);
      const response = await generateAccessCode();
      setNewAccessCode(response.data.code);
      fetchSubscription();
      setIsRedeemingCode(false);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to generate access code');
      console.error('Error generating access code:', err);
      setIsRedeemingCode(false);
    }
  };

  const handleRedeemAccessCode = async (e) => {
    e.preventDefault();
    if (!accessCodeInput.trim()) {
      setError('Please enter a valid access code');
      return;
    }

    try {
      setIsRedeemingCode(true);
      const response = await redeemAccessCode(accessCodeInput);
      setSuccessMessage('Access code redeemed successfully! You now have access to premium content.');
      setAccessCodeInput('');
      fetchSubscription();
      setIsRedeemingCode(false);

      // Clear success message after 5 seconds
      setTimeout(() => {
        setSuccessMessage(null);
      }, 5000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to redeem access code. Please check the code and try again.');
      console.error('Error redeeming access code:', err);
      setIsRedeemingCode(false);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text)
      .then(() => {
        setSuccessMessage('Code copied to clipboard!');
        setTimeout(() => {
          setSuccessMessage(null);
        }, 3000);
      })
      .catch(err => {
        console.error('Failed to copy: ', err);
      });
  };

  const getRemainingCodesCount = () => {
    if (!subscriptionData) return 0;
    const usedCodes = accessCodes.filter(code => code.is_active).length;
    return Math.max(0, subscriptionData.max_access_codes - usedCodes);
  };

  const getActiveUsersCount = () => {
    if (!accessCodes) return 0;
    return accessCodes.filter(code => code.is_active && code.used_by).length;
  };

  const determineAccessType = () => {
    if (subscriptionData && subscriptionData.is_active) {
      return {
        type: 'subscription',
        name: subscriptionData.plan_name,
        expiresAt: new Date(subscriptionData.end_date),
        owner: 'You'
      };
    } else if (newAccessCode) {
      return {
        type: 'access_code',
        name: 'Generated Code',
        expiresAt: new Date(newAccessCode.expires_at),
        owner: 'You'
      };
    }
    return null;
  };

  const AccessInformation = () => {
    const accessInfo = determineAccessType();

    if (!accessInfo) return null;

    return (
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <h3 className="text-lg font-medium text-gray-800 mb-4">How You're Accessing VortexTV</h3>
        <div className="flex items-center mb-4">
          <div className={`w-3 h-3 rounded-full mr-2 ${accessInfo.type === 'subscription' ? 'bg-green-500' : 'bg-blue-500'}`}></div>
          <span className="font-medium">
            {accessInfo.type === 'subscription' ? 'Your Subscription' : 'Generated Access Code'}
          </span>
        </div>

        <div className="space-y-2 text-sm">
          <div className="flex justify-between">
            <span className="text-gray-600">Plan:</span>
            <span className="font-medium">{accessInfo.name}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Expires:</span>
            <span className="font-medium">{accessInfo.expiresAt.toLocaleDateString()}</span>
          </div>
          {accessInfo.type === 'access_code' && (
            <div className="flex justify-between">
              <span className="text-gray-600">Generated By:</span>
              <span className="font-medium">{accessInfo.owner}</span>
            </div>
          )}
          <div className="flex justify-between">
            <span className="text-gray-600">Status:</span>
            <span className="font-medium text-green-600">Active</span>
          </div>
        </div>
      </div>
    );
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    // Validate password
    if (passwordData.new_password.length < 8) {
      setError('New password must be at least 8 characters');
      return;
    }

    if (passwordData.new_password !== passwordData.confirm_password) {
      setError('Passwords do not match');
      return;
    }

    try {
      await updatePassword(passwordData.current_password, passwordData.new_password);
      setSuccessMessage('Password updated successfully');
      setPasswordData({
        current_password: '',
        new_password: '',
        confirm_password: ''
      });
      setShowPasswordForm(false);

      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update password');
      console.error('Error updating password:', err);
    }
  };

  const handlePasswordInputChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-red-600 mx-auto mb-4"></div>
          <div className="text-xl text-gray-700">Loading your profile...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="container mx-auto px-4 max-w-4xl">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">My Profile</h1>
          <button
            onClick={() => navigate('/browse')}
            className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-4 py-2 rounded-md transition-colors duration-200 flex items-center"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 14.707a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 1.414L7.414 9H15a1 1 0 110 2H7.414l2.293 2.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Back to Browse
          </button>
        </div>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6 flex items-start">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 mt-0.5 flex-shrink-0" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
            </svg>
            <div>
              <p className="font-medium">{error}</p>
              <button 
                onClick={() => setError(null)} 
                className="text-sm underline mt-1"
              >
                Dismiss
              </button>
            </div>
          </div>
        )}

        {successMessage && (
          <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-lg mb-6 flex items-start">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 mt-0.5 flex-shrink-0" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            <p className="font-medium">{successMessage}</p>
          </div>
        )}

        {newAccessCode && (
          <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-indigo-200 text-indigo-700 px-6 py-5 rounded-lg mb-6 shadow-sm">
            <div className="flex flex-col md:flex-row md:items-center justify-between">
              <div>
                <p className="font-semibold text-lg mb-2">Access code generated successfully!</p>
                <p className="mb-2">Share this code with a friend:</p>
                <div className="flex items-center">
                  <span className="font-mono bg-white border border-indigo-200 px-4 py-2 rounded text-lg tracking-wider shadow-sm">
                    {newAccessCode}
                  </span>
                  <button 
                    onClick={() => copyToClipboard(newAccessCode)}
                    className="ml-2 bg-indigo-100 hover:bg-indigo-200 text-indigo-700 p-2 rounded transition-colors"
                    title="Copy to clipboard"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path d="M8 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z" />
                      <path d="M6 3a2 2 0 00-2 2v11a2 2 0 002 2h8a2 2 0 002-2V5a2 2 0 00-2-2 3 3 0 01-3 3H9a3 3 0 01-3-3z" />
                    </svg>
                  </button>
                </div>
              </div>
              <button 
                onClick={() => setNewAccessCode(null)}
                className="mt-4 md:mt-0 text-indigo-700 hover:text-indigo-900 underline font-medium"
              >
                Dismiss
              </button>
            </div>
          </div>
        )}

        <AccessInformation />

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Left column - Account Info */}
          <div className="md:col-span-2">
            <div className="bg-white shadow-sm rounded-lg overflow-hidden mb-6">
              <div className="px-6 py-5 border-b border-gray-200 flex justify-between items-center">
                <h2 className="text-xl font-semibold text-gray-800">Account Information</h2>
                {!editMode && (
                  <button
                    onClick={() => setEditMode(true)}
                    className="text-sm bg-gray-100 hover:bg-gray-200 text-gray-700 px-3 py-1 rounded transition-colors"
                  >
                    Edit
                  </button>
                )}
              </div>

              <div className="p-6">
                {editMode ? (
                  <form onSubmit={handleUpdateProfile}>
                    <div className="space-y-4">
                      <div>
                        <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                          Username
                        </label>
                        <input
                          id="username"
                          name="username"
                          type="text"
                          value={formData.username}
                          onChange={handleInputChange}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                        />
                      </div>

                      <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                          Email
                        </label>
                        <input
                          id="email"
                          name="email"
                          type="email"
                          value={formData.email}
                          onChange={handleInputChange}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                        />
                      </div>

                      <div className="flex justify-end space-x-3 pt-2">
                        <button
                          type="button"
                          onClick={() => setEditMode(false)}
                          className="bg-white border border-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-50 transition-colors"
                        >
                          Cancel
                        </button>
                        <button
                          type="submit"
                          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                        >
                          Save Changes
                        </button>
                      </div>
                    </div>
                  </form>
                ) : (
                  <div className="space-y-6">
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                      <div>
                        <h3 className="text-sm font-medium text-gray-500">Username</h3>
                        <p className="mt-1 font-medium text-gray-900">{profile?.username}</p>
                      </div>

                      <div>
                        <h3 className="text-sm font-medium text-gray-500">Email</h3>
                        <p className="mt-1 font-medium text-gray-900">{profile?.email}</p>
                      </div>

                      <div>
                        <h3 className="text-sm font-medium text-gray-500">Account Type</h3>
                        <p className="mt-1 font-medium text-gray-900 capitalize">
                          {profile?.role || 'Standard User'}
                          {subscriptionData?.is_active && 
                            <span className="ml-2 px-2 py-0.5 bg-red-100 text-red-700 text-xs rounded-full">Premium</span>
                          }
                        </p>
                      </div>

                      <div>
                        <h3 className="text-sm font-medium text-gray-500">Member Since</h3>
                        <p className="mt-1 font-medium text-gray-900">
                          {profile?.created_at 
                            ? new Date(profile.created_at).toLocaleDateString('en-US', {
                                year: 'numeric',
                                month: 'long',
                                day: 'numeric'
                              })
                            : 'N/A'}
                        </p>
                      </div>

                      <div>
                        <h3 className="text-sm font-medium text-gray-500">Last Login</h3>
                        <p className="mt-1 font-medium text-gray-900">
                          {profile?.last_login 
                            ? new Date(profile.last_login).toLocaleString('en-US', {
                                year: 'numeric',
                                month: 'long',
                                day: 'numeric',
                                hour: '2-digit',
                                minute: '2-digit'
                              })
                            : 'N/A'}
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Subscription Information */}
            <div className="bg-white shadow-sm rounded-lg overflow-hidden mb-6">
              <div className="px-6 py-5 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-gray-800">
                  {subscriptionData?.is_active ? 'Your Subscription' : 'Subscription Status'}
                </h2>
              </div>

              <div className="p-6">
                {subscriptionData?.is_active ? (
                  <div className="space-y-6">
                    <div className="bg-gradient-to-r from-red-50 to-red-100 rounded-lg p-5 border border-red-200">
                      <div className="flex flex-col md:flex-row md:items-center justify-between">
                        <div>
                          <h3 className="font-bold text-lg text-red-800">{subscriptionData.plan_name} Plan</h3>
                          <p className="text-red-700 mt-1">
                            Valid until {new Date(subscriptionData.end_date).toLocaleDateString('en-US', {
                              year: 'numeric', month: 'long', day: 'numeric'
                            })}
                          </p>
                        </div>
                        <div className="mt-4 md:mt-0">
                          <span className="bg-white px-3 py-1 rounded-full text-red-700 text-sm font-medium border border-red-300">
                            Active
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                      <div className="bg-white border border-gray-200 rounded-lg p-4 text-center">
                        <p className="text-sm text-gray-500 mb-1">Max Sharing</p>
                        <p className="text-2xl font-bold text-gray-900">{subscriptionData.max_access_codes}</p>
                        <p className="text-xs text-gray-500">users</p>
                      </div>
                      <div className="bg-white border border-gray-200 rounded-lg p-4 text-center">
                        <p className="text-sm text-gray-500 mb-1">Available Codes</p>
                        <p className="text-2xl font-bold text-gray-900">{getRemainingCodesCount()}</p>
                        <p className="text-xs text-gray-500">remaining</p>
                      </div>
                      <div className="bg-white border border-gray-200 rounded-lg p-4 text-center">
                        <p className="text-sm text-gray-500 mb-1">Active Users</p>
                        <p className="text-2xl font-bold text-gray-900">{getActiveUsersCount()}</p>
                        <p className="text-xs text-gray-500">using your codes</p>
                      </div>
                    </div>

                    {getRemainingCodesCount() > 0 && (
                      <div className="flex justify-center pt-4">
                        {showGenCodeConfirm ? (
                          <div className="text-center">
                            <p className="mb-3 text-gray-700">Generate a new access code to share with a friend?</p>
                            <div className="flex space-x-3 justify-center">
                              <button
                                onClick={() => setShowGenCodeConfirm(false)}
                                className="bg-gray-200 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-300 transition-colors"
                                disabled={isRedeemingCode}
                              >
                                Cancel
                              </button>
                              <button
                                onClick={() => {
                                  handleGenerateAccessCode();
                                  setShowGenCodeConfirm(false);
                                }}
                                className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors flex items-center"
                                disabled={isRedeemingCode}
                              >
                                {isRedeemingCode ? (
                                  <>
                                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                    </svg>
                                    Generating...
                                  </>
                                ) : (
                                  'Generate Code'
                                )}
                              </button>
                            </div>
                          </div>
                        ) : (
                          <button
                            onClick={() => setShowGenCodeConfirm(true)}
                            className="bg-red-600 text-white px-6 py-3 rounded-md hover:bg-red-700 transition-colors inline-flex items-center shadow-sm"
                          >
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                              <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                            </svg>
                            Generate Access Code
                          </button>
                        )}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="space-y-6">
                    <div className="bg-gray-50 rounded-lg p-5 border border-gray-200">
                      <p className="text-gray-700 mb-4">You don't have an active subscription. Upgrade to access premium features or enter an access code below if you have one.</p>
                      <div className="flex flex-col sm:flex-row sm:space-x-4">
                        <button
                          onClick={() => navigate('/subscriptions')}
                          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors mb-3 sm:mb-0"
                        >
                          View Subscription Plans
                        </button>
                        <button
                          onClick={() => document.getElementById('accessCodeInput').focus()}
                          className="bg-gray-200 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-300 transition-colors"
                        >
                          Use Access Code
                        </button>
                      </div>
                    </div>

                    <div className="border-t border-gray-200 pt-6">
                      <h3 className="text-lg font-medium text-gray-800 mb-4">Redeem Access Code</h3>
                      <p className="text-gray-600 mb-4">
                        If someone has shared their subscription with you, enter the access code below to gain premium access.
                      </p>
                      <form onSubmit={handleRedeemAccessCode} className="flex flex-col sm:flex-row sm:space-x-3">
                        <input
                          id="accessCodeInput"
                          type="text"
                          value={accessCodeInput}
                          onChange={(e) => setAccessCodeInput(e.target.value)}
                          placeholder="Enter access code"
                          className="flex-grow px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500 mb-3 sm:mb-0"
                        />
                        <button
                          type="submit"
                          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors flex justify-center items-center"
                          disabled={isRedeemingCode || !accessCodeInput.trim()}
                        >
                          {isRedeemingCode ? (
                            <>
                              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                              </svg>
                              Redeeming...
                            </>
                          ) : (
                            'Redeem Code'
                          )}
                        </button>
                      </form>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right column */}
          <div className="md:col-span-1">
            {/* Active Access Codes Panel */}
            {accessCodes && accessCodes.length > 0 && (
              <div className="bg-white shadow-sm rounded-lg overflow-hidden mb-6">
                <div className="px-6 py-5 border-b border-gray-200">
                  <h2 className="text-xl font-semibold text-gray-800">Your Access Codes</h2>
                </div>

                <div className="p-4">
                  <ul className="divide-y divide-gray-200">
                    {accessCodes.map(code => (
                      <li key={code.code_id || code.code} className="py-3">
                        <div className="flex justify-between">
                          <div>
                            <div className="flex items-center">
                              <span className="font-mono bg-gray-100 px-2 py-1 rounded text-sm">{code.code}</span>
                              <button 
                                onClick={() => copyToClipboard(code.code)}
                                className="ml-1 text-gray-500 hover:text-gray-700"
                                title="Copy to clipboard"
                              >
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" viewBox="0 0 20 20" fill="currentColor">
                                  <path d="M8 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z" />
                                  <path d="M6 3a2 2 0 00-2 2v11a2 2 0 002 2h8a2 2 0 002-2V5a2 2 0 00-2-2 3 3 0 01-3 3H9a3 3 0 01-3-3z" />
                                </svg>
                              </button>
                            </div>
                            <p className="text-xs text-gray-500 mt-1">
                              {code.expires_at && `Expires: ${new Date(code.expires_at).toLocaleDateString()}`}
                            </p>
                          </div>
                          <div className="flex flex-col items-end">
                            <span 
                              className={`text-xs px-2 py-0.5 rounded-full ${
                                code.is_active 
                                  ? 'bg-green-100 text-green-800' 
                                  : 'bg-gray-100 text-gray-800'
                              }`}
                            >
                              {code.is_active ? 'Active' : 'Inactive'}
                            </span>
                            <span className="text-xs text-gray-500 mt-1">
                              {code.used_by 
                                ? `Used by: ${code.used_by_username || code.used_by}`
                                : 'Not used'
                              }
                            </span>
                          </div>
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            )}

            {/* Account Actions */}
            <div className="bg-white shadow-sm rounded-lg overflow-hidden">
              <div className="px-6 py-5 border-b border-gray-200">
                <h2 className="text-xl font-semibold text-gray-800">Account Actions</h2>
              </div>

              <div className="p-6">
                <div className="space-y-3">
                  {showPasswordForm ? (
                    <div className="bg-white p-4 rounded-lg border border-gray-200 mb-4">
                      <h3 className="text-lg font-medium text-gray-800 mb-4">Change Password</h3>
                      <form onSubmit={handlePasswordChange} className="space-y-4">
                        <div>
                          <label htmlFor="current_password" className="block text-sm font-medium text-gray-700 mb-1">
                            Current Password
                          </label>
                          <input
                            id="current_password"
                            name="current_password"
                            type="password"
                            value={passwordData.current_password}
                            onChange={handlePasswordInputChange}
                            required
                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                          />
                        </div>

                        <div>
                          <label htmlFor="new_password" className="block text-sm font-medium text-gray-700 mb-1">
                            New Password
                          </label>
                          <input
                            id="new_password"
                            name="new_password"
                            type="password"
                            value={passwordData.new_password}
                            onChange={handlePasswordInputChange}
                            required
                            minLength={8}
                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                          />
                          <p className="mt-1 text-xs text-gray-500">Password must be at least 8 characters long</p>
                        </div>

                        <div>
                          <label htmlFor="confirm_password" className="block text-sm font-medium text-gray-700 mb-1">
                            Confirm New Password
                          </label>
                          <input
                            id="confirm_password"
                            name="confirm_password"
                            type="password"
                            value={passwordData.confirm_password}
                            onChange={handlePasswordInputChange}
                            required
                            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                          />
                        </div>

                        <div className="flex justify-end space-x-3 pt-2">
                          <button
                            type="button"
                            onClick={() => setShowPasswordForm(false)}
                            className="bg-white border border-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-50 transition-colors"
                          >
                            Cancel
                          </button>
                          <button
                            type="submit"
                            className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                          >
                            Update Password
                          </button>
                        </div>
                      </form>
                    </div>
                  ) : (
                    <button
                      onClick={() => setShowPasswordForm(true)}
                      className="w-full bg-gray-100 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-200 transition-colors flex items-center justify-center"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M18 8a6 6 0 01-7.743 5.743L10 14l-1 1-1 1H6v-1l1-1 1-1-1.257-1.257A6 6 0 1118 8zm-6-4a1 1 0 10-2 0 2 2 0 012 0z" clipRule="evenodd" />
                      </svg>
                      Change Password
                    </button>
                  )}

                  <button
                    onClick={logout}
                    className="w-full bg-gray-100 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-200 transition-colors flex items-center justify-center"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M3 3a1 1 0 00-1 1v12a1 1 0 001 1h12a1 1 0 001-1V7.414l-5-5H3zm6.293 11.293a1 1 0 001.414 1.414l4-4a1 1 0 000-1.414l-4-4a1 1 0 00-1.414 1.414L11.586 9H5a1 1 0 000 2h6.586l-2.293 2.293z" clipRule="evenodd" />
                    </svg>
                    Log Out
                  </button>

                  <button
                    className="w-full text-red-700 px-4 py-2 rounded-md border border-red-200 hover:bg-red-50 transition-colors flex items-center justify-center"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    Delete Account
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
