import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const AdminSidebar = () => {
  const { user } = useAuth();
  const location = useLocation();
  
  // Check if current path is active
  const isActive = (path) => {
    return location.pathname === path;
  };
  
  return (
    <div className="bg-gray-900 text-white h-screen w-64 fixed left-0 top-0 overflow-y-auto pt-20">
      <div className="px-6 py-4">
        <h2 className="text-xl font-bold text-red-600 mb-6">Admin Panel</h2>
        
        <div className="mb-6">
          <div className="text-gray-400 mb-2 text-sm">Logged in as:</div>
          <div className="font-medium">{user?.username}</div>
          <div className="text-sm text-gray-400">{user?.role}</div>
        </div>
        
        <nav className="space-y-2">
          <Link
            to="/admin"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-colors`}
          >
            <span className="mr-3">üìä</span>
            Dashboard
          </Link>
          
          <Link
            to="/admin/users"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/users') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-colors`}
          >
            <span className="mr-3">üë•</span>
            Users
          </Link>
          
          <Link
            to="/admin/content"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/content') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-colors`}
          >
            <span className="mr-3">üé¨</span>
            Content
          </Link>
          
          <Link
            to="/admin/subscriptions"
            className={`flex items-center py-3 px-4 rounded-md ${
              isActive('/admin/subscriptions') 
                ? 'bg-red-600 text-white' 
                : 'text-gray-300 hover:bg-gray-800 hover:text-white'
            } transition-colors`}
          >
            <span className="mr-3">üí≥</span>
            Subscriptions
          </Link>
        </nav>
      </div>
      
      <div className="px-6 py-4 mt-auto">
        <Link
          to="/browse"
          className="flex items-center py-3 px-4 text-gray-300 hover:bg-gray-800 hover:text-white rounded-md transition-colors"
        >
          <span className="mr-3">üè†</span>
          Back to Site
        </Link>
      </div>
    </div>
  );
};

export default AdminSidebar;
