import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import backendApi from '../api/backendApi';

const Profile = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subscriptionData, setSubscriptionData] = useState(null);
  const [accessCodes, setAccessCodes] = useState([]);
  const [newAccessCode, setNewAccessCode] = useState(null);
  const [showGenCodeConfirm, setShowGenCodeConfirm] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    } else {
      backendApi.setupTokenRefresh();
      fetchProfile();
      fetchSubscription();
    }
  }, [isAuthenticated, navigate]);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const response = await backendApi.getCurrentUser();
      setProfile(response.data);
      setFormData({
        username: response.data.username,
        email: response.data.email
      });
    } catch (err) {
      setError('Failed to load profile information');
      console.error('Error fetching profile:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchSubscription = async () => {
    try {
      const response = await backendApi.getUserSubscription();
      setSubscriptionData(response.data);
      setAccessCodes(response.data.access_codes || []);
    } catch (err) {
      // Only log error, don't set error state since subscription might not exist
      console.error('Error fetching subscription:', err);
    }
  };

  const handleUpdateProfile = async (e) => {
    e.preventDefault();

    try {
      await backendApi.updateUser(user.user_id, formData);
      setEditMode(false);
      fetchProfile();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
      console.error('Error updating profile:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGenerateAccessCode = async () => {
    try {
      const response = await backendApi.generateAccessCode();
      setNewAccessCode(response.data.code);
      fetchSubscription();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to generate access code');
      console.error('Error generating access code:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">Loading profile...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-12">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-3xl font-bold mb-8">My Profile</h1>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
            {error}
          </div>
        )}

        {newAccessCode && (
          <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded mb-6">
            <p className="font-medium">Access code generated successfully!</p>
            <p>Share this code with a friend: <span className="font-mono bg-gray-100 px-2 py-1 rounded">{newAccessCode}</span></p>
            <button 
              onClick={() => setNewAccessCode(null)} 
              className="text-sm text-green-700 underline mt-2"
            >
              Dismiss
            </button>
          </div>
        )}

        <div className="bg-white shadow rounded-lg overflow-hidden mb-8">
          <div className="px-6 py-5 border-b border-gray-200">
            <h2 className="text-xl font-medium">Account Information</h2>
          </div>

          <div className="p-6">
            {editMode ? (
              <form onSubmit={handleUpdateProfile}>
                <div className="space-y-4">
                  <div>
                    <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                      Username
                    </label>
                    <input
                      id="username"
                      name="username"
                      type="text"
                      value={formData.username}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                    />
                  </div>

                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                      Email
                    </label>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                    />
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => setEditMode(false)}
                      className="bg-white border border-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-50 transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                    >
                      Save Changes
                    </button>
                  </div>
                </div>
              </form>
            ) : (
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Username</h3>
                    <p className="mt-1">{profile?.username}</p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Email</h3>
                    <p className="mt-1">{profile?.email}</p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Role</h3>
                    <p className="mt-1 capitalize">{profile?.role_name}</p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Joined</h3>
                    <p className="mt-1">
                      {profile?.created_at 
                        ? new Date(profile.created_at).toLocaleDateString() 
                        : 'N/A'}
                    </p>
                  </div>
                </div>

                <div className="pt-4 flex justify-end">
                  <button
                    onClick={() => setEditMode(true)}
                    className="bg-gray-100 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-200 transition-colors"
                  >
                    Edit Profile
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Subscription Information */}
        <div className="bg-white shadow rounded-lg overflow-hidden mb-8">
          <div className="px-6 py-5 border-b border-gray-200">
            <h2 className="text-xl font-medium text-gray-900">Subscription</h2>
          </div>

          <div className="p-6">
            {subscriptionData ? (
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h3 className="text-sm font-medium text-gray-700">Plan</h3>
                    <p className="mt-1 font-medium text-gray-900">{subscriptionData.plan_name}</p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-700">Status</h3>
                    <p className="mt-1">
                      {subscriptionData.is_active 
                        ? <span className="text-green-700 font-medium">Active</span> 
                        : <span className="text-red-700 font-medium">Inactive</span>}
                    </p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-700">Start Date</h3>
                    <p className="mt-1 text-gray-900">
                      {subscriptionData.start_date 
                        ? new Date(subscriptionData.start_date).toLocaleDateString() 
                        : 'N/A'}
                    </p>
                  </div>

                  <div>
                    <h3 className="text-sm font-medium text-gray-700">Expiry Date</h3>
                    <p className="mt-1 text-gray-900">
                      {subscriptionData.end_date 
                        ? new Date(subscriptionData.end_date).toLocaleDateString() 
                        : 'N/A'}
                    </p>
                  </div>
                </div>

                {/* Access Codes Section */}
                {accessCodes && accessCodes.length > 0 && (
                  <div className="mt-6">
                    <h3 className="text-sm font-medium text-gray-500 mb-3">Access Codes</h3>
                    <div className="bg-gray-50 rounded-md p-4">
                      <ul className="space-y-3">
                        {accessCodes.map(code => (
                          <li key={code.code_id} className="border-b border-gray-200 pb-3 last:border-0 last:pb-0">
                            <div className="flex justify-between items-center">
                              <div>
                                <p className="font-mono">{code.code}</p>
                                <p className="text-sm text-gray-500">
                                  {code.used_by 
                                    ? `Used by: ${code.used_by_username || 'Unknown user'}` 
                                    : 'Not used yet'}
                                </p>
                              </div>
                              <span 
                                className={`text-xs px-2 py-1 rounded-full ${
                                  code.is_active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                                }`}
                              >
                                {code.is_active ? 'Active' : 'Inactive'}
                              </span>
                            </div>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                )}

                <div className="pt-4 flex justify-end">
                  {subscriptionData.is_active && !showGenCodeConfirm && 
                    (accessCodes.filter(c => c.is_active && !c.used_by).length < subscriptionData.max_access_codes) && (
                    <button
                      onClick={() => setShowGenCodeConfirm(true)}
                      className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                    >
                      Generate Access Code
                    </button>
                  )}

                  {showGenCodeConfirm && (
                    <div className="text-right">
                      <p className="mb-2 text-gray-700">Generate an access code to share with a friend?</p>
                      <div className="flex justify-end space-x-3">
                        <button
                          onClick={() => setShowGenCodeConfirm(false)}
                          className="bg-gray-100 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-200 transition-colors"
                        >
                          Cancel
                        </button>
                        <button
                          onClick={() => {
                            handleGenerateAccessCode();
                            setShowGenCodeConfirm(false);
                          }}
                          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                        >
                          Generate
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div className="py-4 text-center">
                <p className="text-gray-700 mb-4">You don't have an active subscription.</p>
                <button
                  onClick={() => navigate('/subscriptions')}
                  className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors"
                >
                  View Subscription Plans
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Account Actions */}
        <div className="bg-white shadow rounded-lg overflow-hidden">
          <div className="px-6 py-5 border-b border-gray-200">
            <h2 className="text-xl font-medium text-gray-900">Account Actions</h2>
          </div>

          <div className="p-6">
            <div className="space-y-4">
              <button
                onClick={logout}
                className="w-full bg-gray-100 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-200 transition-colors"
              >
                Log Out
              </button>

              <button
                className="w-full text-red-700 px-4 py-2 rounded-md border border-red-200 hover:bg-red-50 transition-colors"
              >
                Delete Account
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
