import React, { createContext, useState, useContext, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './hooks/useAuth';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import ForgotPassword from './pages/ForgotPassword';
import ResetPassword from './pages/ResetPassword';
import Browse from './pages/Browse';
import MovieDetails from './pages/MovieDetails';
import TvShowDetails from './pages/TvShowDetails';
import Profile from './pages/Profile';
import Subscriptions from './pages/Subscriptions';
import NotFound from './pages/NotFound';

// Admin Pages
import AdminDashboard from './pages/Admin/Dashboard';
import AdminUsers from './pages/Admin/Users';
import AdminContent from './pages/Admin/Content';
import AdminSubscriptions from './pages/Admin/Subscriptions';
import AdminProfile from './pages/Admin/AdminProfile';

// Layout Components
import Navbar from './components/Layout/Navbar';
import Footer from './components/Layout/Footer';
import AdminSidebar from './components/Layout/AdminSidebar';

// Create a context for sidebar state
export const SidebarContext = createContext();

// SidebarProvider component
export const SidebarProvider = ({ children }) => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <SidebarContext.Provider value={{ isCollapsed, toggleSidebar }}>
      {children}
    </SidebarContext.Provider>
  );
};

// Custom hook to use sidebar context
export const useSidebar = () => {
  const context = useContext(SidebarContext);
  if (!context) {
    throw new Error('useSidebar must be used within a SidebarProvider');
  }
  return context;
};

// Protected Routes Component
const ProtectedRoute = ({ children, requiredRoles = ['user', 'admin', 'superadmin'] }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (!requiredRoles.includes(user.role)) {
    return <Navigate to="/browse" />;
  }

  return children;
};

// Subscription Check Route
const SubscriptionRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  // Allow admins and superadmins to access content without subscription
  if (user.role === 'admin' || user.role === 'superadmin') {
    return children;
  }

  // Regular users need a subscription or access code
  if (!user.hasSubscription && !user.hasAccessCode) {
    return <Navigate to="/subscriptions" />;
  }

  return children;
};

// Admin Route Component
const AdminRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { isCollapsed } = useSidebar();

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (user.role !== 'admin' && user.role !== 'superadmin') {
    return <Navigate to="/browse" />;
  }

  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex flex-grow">
        <AdminSidebar />
        <div className={`flex-1 transition-all duration-300 pt-16 ${isCollapsed ? 'ml-16' : 'ml-64'}`}>
          {children}
        </div>
      </div>
      <div className={`transition-all duration-300 ${isCollapsed ? 'ml-16' : 'ml-64'}`}>
        <Footer />
      </div>
    </div>
  );
};

// SuperAdmin Route Component - Only allows superadmins
const SuperAdminRoute = ({ children }) => {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { isCollapsed } = useSidebar();

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (user.role !== 'superadmin') {
    return <Navigate to="/admin" />;
  }

  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex flex-grow">
        <AdminSidebar />
