import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { backendApi } from '../../api/backendApi';

const AdminSubscriptions = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [totalSubscriptions, setTotalSubscriptions] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [showCreatePlanModal, setShowCreatePlanModal] = useState(false);
  const [showEditPlanModal, setShowEditPlanModal] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [planFormData, setPlanFormData] = useState({
    plan_name: '',
    price: '',
    duration_months: '',
    max_access_codes: '',
    description: '',
    features: ''
  });
  const [activeTab, setActiveTab] = useState('subscriptions');

  useEffect(() => {
    if (activeTab === 'subscriptions') {
      fetchSubscriptions();
    } else {
      fetchPlans();
    }
  }, [activeTab, currentPage, perPage, searchTerm, statusFilter]);

  const fetchSubscriptions = async () => {
    try {
      setLoading(true);

      let url = `/admin/subscriptions?page=${currentPage}&per_page=${perPage}`;

      if (searchTerm) {
        url += `&search=${searchTerm}`;
      }

      if (statusFilter) {
        url += `&status=${statusFilter}`;
      }

      const response = await backendApi.get(url);

      setSubscriptions(response.data.subscriptions);
      setTotalSubscriptions(response.data.total);
    } catch (err) {
      console.error('Error fetching subscriptions:', err);
      setError('Failed to load subscriptions. Please try again later.');

      // Set dummy data for development/preview
      const dummySubscriptions = Array(10).fill().map((_, idx) => ({
        subscription_id: idx + 1,
        user_id: 100 + idx,
        username: `user${100 + idx}`,
        email: `user${100 + idx}@example.com`,
        plan_name: ['Basic', 'Standard', 'Premium'][idx % 3],
        start_date: new Date(Date.now() - (idx * 30 * 86400000)).toISOString(),
        end_date: new Date(Date.now() + ((12 - idx) * 30 * 86400000)).toISOString(),
        is_active: idx < 8,
        payment_status: 'completed'
      }));

      setSubscriptions(dummySubscriptions);
      setTotalSubscriptions(100);
    } finally {
      setLoading(false);
    }
  };

  const fetchPlans = async () => {
    try {
      setLoading(true);
      const response = await backendApi.get('/subscriptions/plans');
      setPlans(response.data);
    } catch (err) {
      console.error('Error fetching plans:', err);
      setError('Failed to load subscription plans. Please try again later.');

      // Set dummy data for development/preview
      setPlans([
        {
          plan_id: 1,
          plan_name: 'Basic',
          price: 7.99,
          duration_months: 1,
          max_access_codes: 1,
          description: 'Basic plan with limited features',
          features: ['HD streaming', 'Watch on 1 device', 'Access to limited content']
        },
        {
          plan_id: 2,
          plan_name: 'Standard',
          price: 13.99,
          duration_months: 1,
          max_access_codes: 2,
          description: 'Standard plan with more features',
          features: ['Full HD streaming', 'Watch on 2 devices', 'Access to all content']
        },
        {
          plan_id: 3,
          plan_name: 'Premium',
          price: 19.99,
          duration_months: 1,
          max_access_codes: 4,
          description: 'Premium plan with all features',
          features: ['4K Ultra HD streaming', 'Watch on 4 devices', 'Access to all content and early releases']
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Search will be triggered by the useEffect dependency
  };

  const handleCreatePlan = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Format features as a semicolon-separated string
      const dataToSend = {...planFormData};

      // Convert multi-line text into semicolon-separated string
      if (typeof dataToSend.features === 'string') {
        dataToSend.features = dataToSend.features
          .split('\n')
          .map(feature => feature.trim())
          .filter(feature => feature !== '')
          .join(';');
      }

      await backendApi.post('/subscriptions/plans', dataToSend);

      // Clear form and close modal
      setPlanFormData({
        plan_name: '',
        price: '',
        duration_months: '',
        max_access_codes: '',
        description: '',
        features: ''
      });
      setShowCreatePlanModal(false);

      // Show success message
      setError(null);

      // Refresh plans
      fetchPlans();
    } catch (err) {
      console.error('Error creating plan:', err);
      setError('Failed to create plan. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleEditPlan = (plan) => {
    // Convert features to array if it's a string
    let featuresArray = [];

    if (typeof plan.features === 'string') {
      featuresArray = plan.features.split(';').filter(feature => feature.trim() !== '');
    } else if (Array.isArray(plan.features)) {
      featuresArray = plan.features;
    }

    setSelectedPlan(plan);
    setPlanFormData({
      plan_name: plan.plan_name || '',
      price: plan.price || '',
      duration_months: plan.duration_months || '',
      max_access_codes: plan.max_access_codes || '',
      description: plan.description || '',
      features: featuresArray.join('\n') // Use newlines for better editing
    });
    setShowEditPlanModal(true);
  };

  const handleUpdatePlan = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Format features as a semicolon-separated string
      const dataToSend = {...planFormData};

      // Convert multi-line text into semicolon-separated string
      if (typeof dataToSend.features === 'string') {
        dataToSend.features = dataToSend.features
          .split('\n')
          .map(feature => feature.trim())
          .filter(feature => feature !== '')
          .join(';');
      }

      await backendApi.put(`/subscriptions/plans/${selectedPlan.plan_id}`, dataToSend);

      // Clear form and close modal
      setPlanFormData({
        plan_name: '',
        price: '',
        duration_months: '',
        max_access_codes: '',
        description: '',
        features: ''
      });
      setShowEditPlanModal(false);
      setSelectedPlan(null);

      // Refresh plans
      fetchPlans();
    } catch (err) {
      console.error('Error updating plan:', err);
      setError('Failed to update plan. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPlanFormData({
      ...planFormData,
      [name]: value
    });
  };

  const handleDeletePlan = async (planId, planName) => {
    if (window.confirm(`Are you sure you want to delete the ${planName} plan? This will affect all users with this subscription.`)) {
      try {
        setLoading(true);
        await backendApi.delete(`/subscriptions/plans/${planId}`);

        // Refresh plans
        fetchPlans();
      } catch (err) {
        console.error('Error deleting plan:', err);
        setError('Failed to delete subscription plan. Please try again later.');
      } finally {
        setLoading(false);
      }
    }
  };

  const totalPages = Math.ceil(totalSubscriptions / perPage);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Subscription Management</h1>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {/* Tabs */}
      <div className="mb-6 border-b border-gray-200">
        <nav className="-mb-px flex space-x-8" aria-label="Tabs">
          <button
            onClick={() => setActiveTab('subscriptions')}
            className={`${
              activeTab === 'subscriptions'
                ? 'border-red-500 text-red-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
          >
            Subscriptions
          </button>
          <button
            onClick={() => setActiveTab('plans')}
            className={`${
              activeTab === 'plans'
                ? 'border-red-500 text-red-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
          >
            Subscription Plans
          </button>
        </nav>
      </div>

      {activeTab === 'subscriptions' ? (
        <>
          {/* Search and Filter */}
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <div className="flex flex-col md:flex-row gap-4 md:items-end">
              <div className="flex-1">
                <form onSubmit={handleSearch}>
                  <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                    Search Subscriptions
                  </label>
                  <div className="flex">
                    <input
                      type="text"
                      id="search"
                      placeholder="Search by username or email"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="flex-1 rounded-l-md border-gray-300 shadow-sm focus:border-red-500 focus:ring-red-500"
                    />
                    <button
                      type="submit"
