import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Browse = () => {
  const [content, setContent] = useState({
    featuredMovie: null,
    trendingMovies: [],
    popularTVShows: [],
    newReleases: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        setLoading(true);
        
        // Fetch trending movies
        const trendingMoviesResponse = await axios.get('http://localhost:5000/api/movies/trending');
        
        // Fetch popular TV shows
        const popularTVShowsResponse = await axios.get('http://localhost:5000/api/tv/popular');
        
        // Set featured movie from first trending movie
        const featuredMovie = trendingMoviesResponse.data[0] || null;
        
        // Get new releases (could be a separate endpoint, but we'll simulate it)
        const newReleasesResponse = await axios.get('http://localhost:5000/api/movies/latest');
        
        setContent({
          featuredMovie,
          trendingMovies: trendingMoviesResponse.data.slice(1, 9), // Skip the featured one
          popularTVShows: popularTVShowsResponse.data.slice(0, 8),
          newReleases: newReleasesResponse.data.slice(0, 8)
        });
      } catch (err) {
        console.error('Error fetching content:', err);
        setError('Failed to load content. Please try again later.');
        
        // Set dummy content for preview purposes
        setContent({
          featuredMovie: {
            id: 1,
            title: 'The Matrix Resurrections',
            backdrop_path: 'https://via.placeholder.com/1200x600?text=The+Matrix+Resurrections',
            overview: 'Return to a world of two realities: one, everyday life; the other, what lies behind it. To find out if his reality is a construct, Neo will have to choose to follow the white rabbit once more.',
            vote_average: 7.8,
            release_date: '2021-12-22'
          },
          trendingMovies: Array(8).fill().map((_, idx) => ({
            id: idx + 2,
            title: `Trending Movie ${idx + 1}`,
            poster_path: `https://via.placeholder.com/300x450?text=Trending+Movie+${idx + 1}`,
            vote_average: (Math.random() * 2 + 7).toFixed(1)
          })),
          popularTVShows: Array(8).fill().map((_, idx) => ({
            id: idx + 1,
            name: `Popular TV Show ${idx + 1}`,
            poster_path: `https://via.placeholder.com/300x450?text=Popular+TV+Show+${idx + 1}`,
            vote_average: (Math.random() * 2 + 7).toFixed(1)
          })),
          newReleases: Array(8).fill().map((_, idx) => ({
            id: idx + 10,
            title: `New Release ${idx + 1}`,
            poster_path: `https://via.placeholder.com/300x450?text=New+Release+${idx + 1}`,
            release_date: '2023-05-15'
          }))
        });
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">Loading content...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Featured Movie Banner */}
      {content.featuredMovie && (
        <div className="relative">
          <div 
            className="aspect-[21/9] bg-cover bg-center"
            style={{ backgroundImage: `url(${content.featuredMovie.backdrop_path})` }}
          >
            <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent" />
            <div className="absolute bottom-0 left-0 p-8 md:p-12 w-full md:w-2/3 text-white">
              <h1 className="text-3xl md:text-4xl font-bold mb-2">{content.featuredMovie.title}</h1>
              <div className="flex items-center mb-4">
                <span className="text-yellow-400 mr-2">★ {content.featuredMovie.vote_average}</span>
                <span>{new Date(content.featuredMovie.release_date).getFullYear()}</span>
              </div>
              <p className="hidden md:block mb-6">{content.featuredMovie.overview}</p>
              <Link
                to={`/movie/${content.featuredMovie.id}`}
                className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded-md font-medium transition-colors inline-block"
              >
                Watch Now
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="container mx-auto px-4 py-12">
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-8">
            {error}
          </div>
        )}

        {/* Trending Movies Section */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold mb-6">Trending Movies</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 md:gap-6">
            {content.trendingMovies.map((movie) => (
              <Link to={`/movie/${movie.id}`} key={movie.id} className="group">
                <div className="relative overflow-hidden rounded-lg mb-2">
                  <img
                    src={movie.poster_path}
                    alt={movie.title}
                    className="w-full h-auto transition-transform duration-300 group-hover:scale-105"
                  />
                  <div className="absolute top-0 right-0 bg-black bg-opacity-75 text-yellow-400 p-1 text-sm">
                    ★ {movie.vote_average}
                  </div>
                </div>
                <h3 className="font-medium truncate">{movie.title}</h3>
              </Link>
            ))}
          </div>
        </section>

        {/* Popular TV Shows Section */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold mb-6">Popular TV Shows</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 md:gap-6">
            {content.popularTVShows.map((show) => (
              <Link to={`/tv/${show.id}`} key={show.id} className="group">
                <div className="relative overflow-hidden rounded-lg mb-2">
                  <img
                    src={show.poster_path}
                    alt={show.name}
                    className="w-full h-auto transition-transform duration-300 group-hover:scale-105"
                  />
                  <div className="absolute top-0 right-0 bg-black bg-opacity-75 text-yellow-400 p-1 text-sm">
                    ★ {show.vote_average}
                  </div>
                </div>
                <h3 className="font-medium truncate">{show.name}</h3>
              </Link>
            ))}
          </div>
        </section>

        {/* New Releases Section */}
        <section>
          <h2 className="text-2xl font-bold mb-6">New Releases</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 md:gap-6">
            {content.newReleases.map((movie) => (
              <Link to={`/movie/${movie.id}`} key={movie.id} className="group">
                <div className="relative overflow-hidden rounded-lg mb-2">
                  <img
                    src={movie.poster_path}
                    alt={movie.title}
                    className="w-full h-auto transition-transform duration-300 group-hover:scale-105"
                  />
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black to-transparent p-2">
                    <span className="text-white text-sm">
                      {new Date(movie.release_date).toLocaleDateString(undefined, { month: 'short', year: 'numeric' })}
                    </span>
                  </div>
                </div>
                <h3 className="font-medium truncate">{movie.title}</h3>
              </Link>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Browse;
