import React, { useState, useEffect } from 'react';
import { getDashboardStats, getFeaturedContent } from '../../api/backendApi';
import { useAuth } from '../../context/AuthContext';
import { formatNumber } from '../../utils/format';
import { motion } from 'framer-motion';
import { FaUsers, FaFilm, FaStar, FaChartLine, FaPlay, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';

const Dashboard = () => {
    const { user } = useAuth();
    const [stats, setStats] = useState(null);
    const [featuredContent, setFeaturedContent] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [featuredError, setFeaturedError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [statsData, featuredData] = await Promise.all([
                    getDashboardStats(),
                    getFeaturedContent()
                ]);
                setStats(statsData);
                setFeaturedContent(featuredData);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-red-500 text-xl">{error}</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            {/* Welcome Section */}
            <div className="mb-8">
                <h1 className="text-3xl font-bold text-gray-800">Welcome back, {user?.username}!</h1>
                <p className="text-gray-600 mt-2">Here's what's happening with your platform today.</p>
            </div>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                <motion.div 
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                    className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow"
                >
                    <div className="flex items-center">
                        <div className="p-3 bg-blue-100 rounded-lg">
                            <FaUsers className="text-blue-500 text-2xl" />
                        </div>
                        <div className="ml-4">
                            <p className="text-gray-500 text-sm">Total Users</p>
                            <h3 className="text-2xl font-bold text-gray-800">{formatNumber(stats?.total_users || 0)}</h3>
                        </div>
                    </div>
                </motion.div>

                <motion.div 
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.1 }}
                    className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow"
                >
                    <div className="flex items-center">
                        <div className="p-3 bg-green-100 rounded-lg">
                            <FaFilm className="text-green-500 text-2xl" />
                        </div>
                        <div className="ml-4">
                            <p className="text-gray-500 text-sm">Total Content</p>
                            <h3 className="text-2xl font-bold text-gray-800">{formatNumber(stats?.total_content || 0)}</h3>
                        </div>
                    </div>
                </motion.div>

                <motion.div 
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.2 }}
                    className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow"
                >
                    <div className="flex items-center">
                        <div className="p-3 bg-purple-100 rounded-lg">
                            <FaStar className="text-purple-500 text-2xl" />
                        </div>
                        <div className="ml-4">
                            <p className="text-gray-500 text-sm">Featured Content</p>
                            <h3 className="text-2xl font-bold text-gray-800">{formatNumber(stats?.featured_content || 0)}</h3>
                        </div>
                    </div>
                </motion.div>

                <motion.div 
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.3 }}
                    className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow"
                >
                    <div className="flex items-center">
                        <div className="p-3 bg-yellow-100 rounded-lg">
                            <FaChartLine className="text-yellow-500 text-2xl" />
                        </div>
                        <div className="ml-4">
                            <p className="text-gray-500 text-sm">Total Views</p>
                            <h3 className="text-2xl font-bold text-gray-800">{formatNumber(stats?.total_views || 0)}</h3>
                        </div>
                    </div>
                </motion.div>
            </div>

            {/* Featured Content Section */}
            <div className="mb-8">
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold text-gray-800">Featured Content</h2>
                    <button className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors flex items-center">
                        <FaPlus className="mr-2" />
                        Add Featured
                    </button>
                </div>

                {featuredError && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                        {featuredError}
                    </div>
                )}

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                    {featuredContent.map((item) => (
                        <motion.div
                            key={item.content_id}
                            initial={{ opacity: 0, scale: 0.95 }}
                            animate={{ opacity: 1, scale: 1 }}
                            whileHover={{ scale: 1.02 }}
                            className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow"
                        >
                            <div className="relative">
                                <img
                                    src={item.poster_url || '/placeholder-poster.jpg'}
                                    alt={item.title}
                                    className="w-full h-48 object-cover"
                                    onError={(e) => {
                                        e.target.src = '/placeholder-poster.jpg';
                                    }}
                                />
                                <div className="absolute top-2 right-2 flex space-x-2">
                                    <button className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600 transition-colors">
                                        <FaEdit />
                                    </button>
                                    <button className="bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors">
                                        <FaTrash />
                                    </button>
                                </div>
                            </div>
                            <div className="p-4">
                                <h3 className="text-lg font-semibold text-gray-800 mb-2">{item.title}</h3>
                                <p className="text-gray-600 text-sm mb-2">{item.description}</p>
                                <div className="flex items-center justify-between">
                                    <span className="text-sm text-gray-500">{item.release_year}</span>
                                    <div className="flex items-center">
                                        <FaStar className="text-yellow-500 mr-1" />
                                        <span className="text-sm font-medium">{item.rating}</span>
                                    </div>
                                </div>
                                <div className="mt-2 flex items-center text-sm text-gray-500">
                                    <FaPlay className="mr-1" />
                                    <span>{formatNumber(item.view_count)} views</span>
                                </div>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>

            {/* Subscription Plans Section */}
            <div>
                <h2 className="text-2xl font-bold text-gray-800 mb-6">Subscription Plans</h2>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {stats?.subscription_plans?.map((plan) => (
                        <motion.div
                            key={plan.plan_id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            whileHover={{ y: -5 }}
                            className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow"
                        >
                            <div className="text-center mb-6">
                                <h3 className="text-xl font-bold text-gray-800 mb-2">{plan.name}</h3>
                                <div className="text-3xl font-bold text-blue-500">
                                    ${plan.price}
                                    <span className="text-sm text-gray-500">/month</span>
                                </div>
                            </div>
                            <ul className="space-y-3 mb-6">
                                {plan.features.map((feature, index) => (
                                    <li key={index} className="flex items-center text-gray-600">
                                        <FaPlus className="text-green-500 mr-2" />
                                        {feature}
                                    </li>
                                ))}
                            </ul>
                            <button className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition-colors">
                                Manage Plan
                            </button>
                        </motion.div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
