import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    // Add event listener
    window.addEventListener('scroll', handleScroll);
    
    // Clean up event listener
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <nav 
      className={`${
        scrolled 
          ? 'bg-gray-900 bg-opacity-95 shadow-lg py-2' 
          : 'bg-gray-900 py-3'
      } text-white fixed top-0 left-0 right-0 z-50 transition-all duration-300`}
    >
      <div className="container mx-auto px-4 flex justify-between items-center">
        <div className="flex items-center">
          <Link 
            to={isAuthenticated ? "/browse" : "/"} 
            className={`${
              scrolled ? 'text-xl' : 'text-2xl'
            } font-bold text-red-600 transition-all duration-300`}
          >
            VortexTV
          </Link>
          
          {isAuthenticated && (
            <div className="ml-10 space-x-6">
              <Link to="/browse" className="hover:text-red-500 transition-colors">Browse</Link>
              {!(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link to="/subscriptions" className="hover:text-red-500 transition-colors">Subscriptions</Link>
              )}
              {(user?.role === 'admin' || user?.role === 'superadmin') && (
                <Link to="/admin" className="hover:text-red-500 transition-colors">Admin Panel</Link>
              )}
            </div>
          )}
        </div>
        
        <div className="flex items-center space-x-4">
          {isAuthenticated ? (
            <>
              <Link to="/profile" className="hover:text-red-500 transition-colors">
                Profile
              </Link>
              <button 
                onClick={logout}
                className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded-md transition-colors"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-red-500 transition-colors">
                Login
              </Link>
              <Link to="/register" className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded-md transition-colors">
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
