import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode';

// Create auth context
export const AuthContext = createContext();

// Provider component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Check if user is already logged in on mount
  useEffect(() => {
    checkUserLoggedIn();
  }, []);

  // Check if token exists and is valid
  const checkUserLoggedIn = async () => {
    setIsLoading(true);
    const token = localStorage.getItem('token');
    
    if (!token) {
      setIsLoading(false);
      return;
    }
    
    try {
      // Check if token is expired
      const decoded = jwtDecode(token);
      const currentTime = Date.now() / 1000;
      
      if (decoded.exp < currentTime) {
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        setUser(null);
        setIsLoading(false);
        return;
      }
      
      // Set authorization header in axios instance instead of defaults
      const response = await axios.get('http://localhost:5000/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      setUser(response.data);
      setIsAuthenticated(true);
    } catch (error) {
      console.error('Error checking authentication:', error);
      localStorage.removeItem('token');
      setUser(null);
      setIsAuthenticated(false);
    }
    
    setIsLoading(false);
  };

  // Register user
  const register = async (userData) => {
    setIsLoading(true);
    setError(null);
    
    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', userData);
      
      const token = response.data.token;
      localStorage.setItem('token', token);
      
      // Set user data manually instead of calling checkUserLoggedIn
      // This avoids an extra API call that might fail
      const userId = response.data.user_id; 
      setUser({
        user_id: userId,
        username: userData.username,
        email: userData.email,
        role: 'user' // Default role for new registrations
      });
      setIsAuthenticated(true);
      
      navigate('/subscriptions');
      return true;
    } catch (error) {
      console.error('Registration error:', error);
      setError(error.response?.data?.message || 'Registration failed');
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  // Login user
  const login = async (credentials) => {
    setIsLoading(true);
    setError(null);
    
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', credentials);
      
      const token = response.data.token;
      localStorage.setItem('token', token);
      
      // Get current user data after login with proper authorization header
      const userResponse = await axios.get('http://localhost:5000/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      const userData = userResponse.data;
      setUser(userData);
      setIsAuthenticated(true);
      
      // Redirect based on user role
      if (userData.role === 'admin' || userData.role === 'superadmin') {
        navigate('/admin');
      } else {
        navigate('/browse');
      }
      
      return true;
    } catch (error) {
      console.error('Login error:', error);
      setError(error.response?.data?.message || 'Login failed');
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  // Logout user
  const logout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login');
  };

  // Check if user has active subscription
  const checkSubscription = async () => {
    if (!isAuthenticated) return false;
    
    try {
      const response = await axios.get('http://localhost:5000/api/subscriptions/check');
      
      // Update user data with subscription info
      setUser(prevUser => ({
        ...prevUser,
        hasSubscription: response.data.hasSubscription,
        hasAccessCode: response.data.hasAccessCode,
        subscription: response.data.subscription
      }));
      
      return response.data.hasSubscription || response.data.hasAccessCode;
    } catch (error) {
      console.error('Error checking subscription:', error);
      return false;
    }
  };

  // Generate access code
  const generateAccessCode = async () => {
    if (!isAuthenticated || !user.hasSubscription) return { success: false };
    
    try {
      const response = await axios.post('http://localhost:5000/api/access-codes/generate');
      return { success: true, code: response.data.code };
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to generate access code');
      return { success: false };
    }
  };

  // Redeem access code
  const redeemAccessCode = async (code) => {
    try {
      const response = await axios.post('http://localhost:5000/api/access-codes/redeem', { code });
      
      // Update user data with access code info
      setUser(prevUser => ({
        ...prevUser,
        hasAccessCode: true,
        accessCode: response.data.accessCode
      }));
      
      return { success: true };
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to redeem access code');
      return { success: false, error: error.response?.data?.message };
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated,
        isLoading,
        error,
        register,
        login,
        logout,
        checkSubscription,
        generateAccessCode,
        redeemAccessCode
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
