import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import tmdbApi, { getImageUrl } from '../api/tmdbApi';

const Home = () => {
  const [featuredContent, setFeaturedContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeaturedContent = async () => {
      try {
        setLoading(true);
        // Fetch trending movies and popular TV shows
        const [trendingMovies, popularTVShows] = await Promise.all([
          tmdbApi.getTrendingMovies(),
          tmdbApi.getPopularTvShows()
        ]);

        // Combine and format the content
        const combinedContent = [
          ...trendingMovies.slice(0, 2).map(movie => ({
            id: movie.id,
            type: 'movie',
            title: movie.title,
            image: getImageUrl(movie.poster_path, 'w500')
          })),
          ...popularTVShows.slice(0, 2).map(show => ({
            id: show.id,
            type: 'tv',
            title: show.name,
            image: getImageUrl(show.poster_path, 'w500')
          }))
        ];

        setFeaturedContent(combinedContent);
        setError(null);
      } catch (err) {
        console.error('Error fetching featured content:', err);
        setError('Failed to load featured content. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedContent();
  }, []);

  return (
    <div>
      {/* Hero Section */}
      <div className="bg-gray-900 text-white">
        <div className="container mx-auto px-4 py-24 flex flex-col items-center text-center">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">Welcome to VortexTV</h1>
          <p className="text-xl max-w-2xl mb-10">
            Your ultimate streaming platform for movies and TV shows.
            Watch unlimited content with our subscription plans.
          </p>
          <div className="flex flex-col sm:flex-row gap-4">
            <Link
              to="/register"
              className="bg-red-600 hover:bg-red-700 text-white px-8 py-3 rounded-md font-medium transition-colors"
            >
              Sign Up Now
            </Link>
            <Link
              to="/subscriptions"
              className="bg-transparent border border-white hover:bg-white hover:text-gray-900 text-white px-8 py-3 rounded-md font-medium transition-colors"
            >
              View Plans
            </Link>
          </div>
        </div>
      </div>

      {/* Featured Content Section */}
      <div className="container mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold mb-8">Featured Content</h2>
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
            {error}
          </div>
        )}
        {loading ? (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {[...Array(4)].map((_, index) => (
              <div key={index} className="animate-pulse">
                <div className="bg-gray-200 rounded-lg h-[450px] mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-1/4"></div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {featuredContent.map((item) => (
              <div key={item.id} className="group">
                <div className="relative overflow-hidden rounded-lg mb-2">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="w-full h-auto transition-transform duration-300 group-hover:scale-105"
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-50 transition-opacity flex items-center justify-center">
                    <Link
                      to={`/${item.type}/${item.id}`}
                      className="bg-red-600 text-white px-4 py-2 rounded opacity-0 group-hover:opacity-100 transition-opacity"
                    >
                      Watch Now
                    </Link>
                  </div>
                </div>
                <h3 className="font-medium">{item.title}</h3>
                <p className="text-gray-500 text-sm capitalize">{item.type}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Subscription Plans Teaser */}
      <div className="bg-gray-100 py-16">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-8 text-center">Choose Your Plan</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="bg-white p-8 rounded-lg shadow-md flex flex-col">
              <h3 className="text-xl font-bold mb-4">Basic</h3>
              <p className="text-3xl font-bold mb-6">$7.99<span className="text-sm font-normal text-gray-500">/month</span></p>
              <ul className="mb-8 flex-grow">
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> HD streaming</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Watch on 1 device</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Limited content</li>
              </ul>
              <Link to="/subscriptions" className="bg-red-600 hover:bg-red-700 text-white text-center py-2 rounded-md transition-colors">
                Get Started
              </Link>
            </div>

            <div className="bg-white p-8 rounded-lg shadow-md flex flex-col relative">
              <div className="absolute top-0 right-0 bg-red-600 text-white px-4 py-1 rounded-bl-lg rounded-tr-lg text-sm font-medium">
                POPULAR
              </div>
              <h3 className="text-xl font-bold mb-4">Standard</h3>
