import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import axios from 'axios';

const Subscriptions = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subscribing, setSubscribing] = useState(false);
  const [activeSubscription, setActiveSubscription] = useState(null);
  const { isAuthenticated, user, checkSubscription } = useAuth();
  const navigate = useNavigate();
  const [successMessage, setSuccessMessage] = useState(null);

  useEffect(() => {
    fetchSubscriptionPlans();
    if (isAuthenticated) {
      fetchUserSubscription();
    }
  }, [isAuthenticated]);

  const fetchSubscriptionPlans = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/subscriptions/plans');
      setPlans(response.data);
    } catch (err) {
      setError('Failed to load subscription plans. Please try again later.');
      console.error('Error fetching plans:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserSubscription = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/subscriptions/me');
      setActiveSubscription(response.data);
    } catch (err) {
      // Only set error if it's not a 404 (no subscription found)
      if (err.response && err.response.status !== 404) {
        setError('Failed to load your subscription. Please try again later.');
        console.error('Error fetching user subscription:', err);
      }
    }
  };

  const handleSubscribe = async (planId) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      setSubscribing(true);
      await axios.post('http://localhost:5000/api/subscriptions', { plan_id: planId });
      
      // Update user subscription status
      await checkSubscription();
      
      // Fetch the updated subscription
      await fetchUserSubscription();
      
      // Navigate to browse page
      navigate('/browse');
    } catch (err) {
      setError('Failed to process subscription. Please try again later.');
      console.error('Error subscribing:', err);
    } finally {
      setSubscribing(false);
    }
  };

  const handleCancelSubscription = async () => {
    // Prompt user to confirm cancellation
    if (!window.confirm('Are you sure you want to cancel your subscription? This action cannot be undone.')) {
      return;
    }
    
    try {
      setSubscribing(true);
      setError(null);
      
      // Call the API to cancel the subscription
      const response = await axios.delete('http://localhost:5000/api/subscriptions/me');
      
      // Update local state to reflect the change immediately
      setActiveSubscription(null);
      await checkSubscription();
      
      // Show success message
      setSuccessMessage('Your subscription has been successfully cancelled and will no longer be active.');
      
      // Clear success message after 5 seconds
      setTimeout(() => {
        setSuccessMessage(null);
      }, 5000);
      
    } catch (err) {
      setError('Failed to cancel subscription. Please try again later.');
      console.error('Error cancelling subscription:', err);
    } finally {
      setSubscribing(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-xl">Loading subscription plans...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-12">
      <div className="text-center mb-12">
        <h1 className="text-3xl md:text-4xl font-bold mb-4 text-gray-900">Choose Your Subscription Plan</h1>
        <p className="text-lg text-gray-700 max-w-2xl mx-auto">
          Get access to our entire library of movies and TV shows with a VortexTV subscription.
        </p>
      </div>

      {error && (
        <div className="max-w-md mx-auto mb-8 bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      )}

      {successMessage && (
        <div className="max-w-md mx-auto mb-8 bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded">
          {successMessage}
        </div>
      )}

      {activeSubscription && (
        <div className="max-w-2xl mx-auto mb-12 bg-gray-50 border border-gray-200 p-6 rounded-lg">
          <h2 className="text-xl font-bold mb-2 text-gray-900">Your Current Subscription</h2>
          <div className="mb-4">
            <p className="font-medium text-gray-800">{activeSubscription.plan_name}</p>
            <p className="text-gray-700">
              Expires on: {new Date(activeSubscription.end_date).toLocaleDateString()}
            </p>
            <p className="text-gray-700">
              Status: {activeSubscription.is_active ? 'Active' : 'Inactive'}
            </p>
          </div>
          <button
            onClick={handleCancelSubscription}
            disabled={subscribing}
            className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-4 py-2 rounded transition-colors"
          >
            {subscribing ? 'Processing...' : 'Cancel Subscription'}
          </button>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {plans.map((plan) => (
          <div 
            key={plan.plan_id} 
            className={`bg-white p-8 rounded-lg shadow-md flex flex-col ${
              plan.plan_name === 'Standard' ? 'border-2 border-red-500' : ''
            }`}
          >
            {plan.plan_name === 'Standard' && (
              <div className="absolute top-0 right-0 bg-red-600 text-white px-4 py-1 rounded-bl-lg rounded-tr-lg text-sm font-medium">
                POPULAR
              </div>
            )}
            <h3 className="text-xl font-bold mb-2 text-gray-900">{plan.plan_name}</h3>
            <p className="text-3xl font-bold mb-4 text-gray-900">${plan.price}<span className="text-sm font-normal text-gray-600">/month</span></p>
            <p className="text-gray-700 mb-6">{plan.description}</p>
            
            <ul className="mb-8 flex-grow">
              {plan.features.map((feature, index) => (
                <li key={index} className="mb-2 flex items-center text-gray-800">
                  <span className="mr-2 text-green-600">âœ“</span> {feature}
                </li>
              ))}
            </ul>
            
            <button
              onClick={() => handleSubscribe(plan.plan_id)}
              disabled={subscribing || (activeSubscription && activeSubscription.plan_name === plan.plan_name)}
              className={`w-full py-2 px-4 rounded font-medium ${
                activeSubscription && activeSubscription.plan_name === plan.plan_name
                  ? 'bg-green-600 text-white cursor-default'
                  : 'bg-red-600 hover:bg-red-700 text-white transition-colors'
              }`}
            >
              {subscribing
                ? 'Processing...'
                : activeSubscription && activeSubscription.plan_name === plan.plan_name
                ? 'Current Plan'
                : 'Select Plan'}
            </button>
          </div>
        ))}
      </div>

      <div className="mt-16 text-center max-w-2xl mx-auto">
        <h3 className="text-xl font-bold mb-4 text-gray-900">All Plans Include:</h3>
        <ul className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <li className="bg-gray-50 p-4 rounded">
            <div className="font-medium mb-2 text-gray-800">No Contracts</div>
            <p className="text-gray-700">Cancel anytime, no hidden fees</p>
          </li>
          <li className="bg-gray-50 p-4 rounded">
            <div className="font-medium mb-2 text-gray-800">All Content</div>
            <p className="text-gray-700">Access to thousands of titles</p>
          </li>
          <li className="bg-gray-50 p-4 rounded">
            <div className="font-medium mb-2 text-gray-800">No Ads</div>
            <p className="text-gray-700">Uninterrupted streaming experience</p>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Subscriptions;
