              <ul className="mb-8 flex-grow">
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> HD streaming</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Watch on 1 device</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Limited content</li>
              </ul>
              <Link to="/subscriptions" className="bg-red-600 hover:bg-red-700 text-white text-center py-2 rounded-md transition-colors">
                Get Started
              </Link>
            </div>

            <div className="bg-white p-8 rounded-lg shadow-md flex flex-col relative">
              <div className="absolute top-0 right-0 bg-red-600 text-white px-4 py-1 rounded-bl-lg rounded-tr-lg text-sm font-medium">
                POPULAR
              </div>
              <h3 className="text-xl font-bold mb-4">Standard</h3>
              <p className="text-3xl font-bold mb-6">$13.99<span className="text-sm font-normal text-gray-500">/month</span></p>
              <ul className="mb-8 flex-grow">
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Full HD streaming</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Watch on 2 devices</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> All content</li>
              </ul>
              <Link to="/subscriptions" className="bg-red-600 hover:bg-red-700 text-white text-center py-2 rounded-md transition-colors">
                Get Started
              </Link>
            </div>

            <div className="bg-white p-8 rounded-lg shadow-md flex flex-col">
              <h3 className="text-xl font-bold mb-4">Premium</h3>
              <p className="text-3xl font-bold mb-6">$19.99<span className="text-sm font-normal text-gray-500">/month</span></p>
              <ul className="mb-8 flex-grow">
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> 4K Ultra HD streaming</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Watch on 4 devices</li>
                <li className="mb-2 flex items-center"><span className="mr-2 text-green-500">✓</span> Early access to new releases</li>
              </ul>
              <Link to="/subscriptions" className="bg-red-600 hover:bg-red-700 text-white text-center py-2 rounded-md transition-colors">
                Get Started
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
